function Doctorsection() {
  const url = useContext(URL);
  const APIDate = useContext(TodayDate)

  const month = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December",];
  var weekday = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday",];
  const d = new Date();
  const date = d.getDate() < 10 ? '0' + d.getDate() : d.getDate();
  const monthcount = d.getMonth() + 1;
  const yearcount = d.getFullYear();
  let monthname = month[d.getMonth()];
  var weekname = weekday[d.getDay()];
  var fullDate = new Date();
  var currentDate = "Today" + " " + monthname + " " + fullDate.getDate() + "," + fullDate.getFullYear() + " ";

  let Doctorarray = [];
  let TodayDoctors = [];
  const [isLoading, setIsLoading] = useState(false);
  const [docapi, setdocapi] = useState([]);
  const [todayDoc, settodaydoc] = useState([]);
  async function fetchapi() {
    setIsLoading(true);
    await axios.get("/doctor/list").then(function (response) {
      let tempArray = response.data.data;
      for (let i = 0; i < tempArray.length; i++) {
        Doctorarray.push([tempArray[i].id, tempArray[i].doctor_name, []]);
        for (var j = 0; j < tempArray[i].month_timeslots.length; j++) {
          if (tempArray[i].month_timeslots[j].date === APIDate) {
            Doctorarray[i][2].push([[tempArray[i].month_timeslots[j].time_from], [tempArray[i].month_timeslots[j].booking_status]])
          }
        }
      }
      for (var q = 0; q < Doctorarray.length; q++) {
        if (Doctorarray[q][2].length != 0) {
          TodayDoctors.push(Doctorarray[q])
          settodaydoc(TodayDoctors)
        }
      }
      if (TodayDoctors.length == 0) {
        TodayDoctors.push(['0', 'No Doctors Found', ['No Time Slots Found']]);
        settodaydoc(TodayDoctors);
      }
      setdocapi(Doctorarray);
    });
    setIsLoading(false);
  }

  useEffect(() => {
    fetchapi()
  }, [])

  const [Doctor, setDoctor] = useState(0);
  function _selectedDoctor(_selected) {
    for (var s = 0; s < docapi.length; s++) {
      if (_selected === s && isLoading == false) {
        return (

          <>
            <div className="p-0 m-0">
              <Timecard docid={todayDoc[_selected][0]} _selected={_selected} />
            </div>
            <div className="p-0 m-0">
              <DoctorSchedule todayDoc={todayDoc} _selected={_selected} />
            </div>
            <hr />
          </>
        );

      }
    }
  }

  function Time() {
    const [time, setTime] = useState(new Date());
    useEffect(() => {
      setInterval(() => {
        setTime(new Date());
      }, 1000);
    }, []);

    return (
      <span>
        {time.toLocaleString("en-US", {
          timeStyle: "medium",
          hour12: false,
        })}{" "}
      </span>
    );
  }

  const [doctorform, setdoctorform] = useState("none");
  const toggledoctorform = () => {
    if (doctorform === "none") {
      setDocval(1)
      setdoctorform("block");
    }
    if (doctorform === "block") {
      setDocval(0)
      setdoctorform("none");
    }
  };


  const [Docval, setDocval] = useState(0)
  return (
    <>
      <div className="container-fluid doctorcontainer">
        <section className="doctorsection ">
          <div className="row shadow-sm">
            <div className="d-flex scroll doctortab">
              {isLoading ? (
                <> <div className="spinner-border my-auto" style={{ width: "2rem", height: "2rem" }} role="status" >
                  <span className="sr-only"></span> </div>
                  <div className="spinner-grow my-auto" style={{ width: "1.5rem", height: "1.5rem" }} role="status" >
                    <span className="sr-only"></span> </div> </>
              ) : (
                todayDoc.map((data, i) => (
                  <>
                    <button className={`btn m-1 doctorbutton text-${i === Doctor ? "light" : "dark"} bg-${i === Doctor ? "charcoal" : "seashell"}`} onClick={(a) => { setDoctor(i) }}>{`${data[0]}.${data[1]}`}</button>
                  </>
                ))
              )}
              <button className="btn bg-transparent border-0 d-inline-flex" id="adddoctorbtn" onClick={toggledoctorform} >
                <img src={process.env.PUBLIC_URL + "/images/addicon.png"} alt="displaying_image" style={{ width: "2.5rem" }} />
              </button>
            </div>
          </div>
        </section>
      </div>
      <section className="patientsection border-start border-5 border-dark border-opacity-50 position-relative">
        <div className="container-fluid">
          <div className="col-12">
            <div className="row">
              <div className="col-9">
                <div className="p-1" id="calender">
                  {currentDate}
                </div>
              </div>
              <div className="col-3">
                <Draggable className="p-1 align-items-center">
                  <div className="text-black align-items-center" id="livetime">
                    {Time()}
                  </div>
                </Draggable>
              </div>
            </div>
          </div>
        </div>
        {todayDoc.length == 0 ? (
          <div className="container-fliud pt-3">
            <div className="d-flex fs-2 align-items-center justify-content-around">
              <strong className="text-burntumber">Please Wait...</strong>
              <div
                className="text-burntumber spinner-border ml-auto"
                role="status"
                aria-hidden="true"
              ></div>
            </div>
          </div>
        ) : (
          _selectedDoctor(Doctor)
        )}
      </section>
      {
        Docval == 1 ? (
          <div className={`col-lg-5 col-md-6 col-sm-12 col-12 doctorinfosection d-${doctorform}  border-start border-top border-2 position-absolute`} >
            <AddDoctorSlot toggledoctorform={toggledoctorform} staticBackdrop4={'staticBackdrop4'} fetchapi={fetchapi} />
          </div>
        ) : (
          <div></div>
        )
      }

    </>
  );

}


function AllAppointments() {
  const url = useContext(URL)
  const docnames = useContext(DoctorsList)
  const [isLoading, setisLoading] = useState()
  const [appointmentdata, setappointmentdata] = useState([])
  const [doctorid, setdoctorid] = useState()
  const [fromdate, setfromdate] = useState()
  const [todate, settodate] = useState()


  function tConvert(time) {

    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) {
      time = time.slice(1);
      time[3] = +time[0] < 12 ? ' AM ' : ' PM ';
      time[0] = +time[0] % 12 || 12;
    }
    return time.join('');
  }


  let array = [[1,'Pending','lightred'],[2,'Booked','lightblue'],[3,'Cancelled','lightred'],[4,'QR Generated','light'],[5,'Checked_in','brandy'],[6,'Vitals Done','lightred'],[7,'In_apppointment','lightyellow'],[8,'Payment done','success'],[9,'Unattained','lightyellow'],[10,'Completed','lightgreen']] 

   function status(number){
    let status
    for (let i=0;i<array.length;i++){
      if(number == array[i][0]){
        status =  array[i][1]
        break;
      }
    }
    return status
  }

  function status_color(number){
    let status_color;
        for(let j=0;j<array.length;j++){
          if(number == array[j][0]){
            status_color = array[j][2]
              break;
          }
        }
        return status_color
  }

  async function Appointmentlist() {
    setisLoading(true);
    await axios.get(`${url}/appointment/list?doctor_id=${doctorid}&from_date=${fromdate}&to_date=${todate}`).then((response) => {
      setappointmentdata(response.data.data);
      console.log(response.data.data)
    })
