  const toggle_npef = () => {
    if (npef === "none") {
      setnpef("block");
      setpharmacy("none");
    }
    if (npef === "block") {
      setnpef("none");
      setpharmacy("block");
    }
  };
  const toggle_peidw = () => {
    if (peidw === "none") {
      setpeidw("block");
      setpharmacy("none");
    }
    if (peidw === "block") {
      setpeidw("none");
      setpharmacy("block");
    }
  };
  const _selectedmenu = (_menu) => {
    if (_menu === 0) {
      return <div className="">{_menu}</div>;
    }
    if (_menu === 1) {
      return (
        <div className="">
          <Stocksection />
        </div>
      );
