import { useState} from "react";
import '../../css/bootstrap.css';
import {Purchaseentryarray,Pharmacystocktable} from './apiarrays';
import{Purchaseorderarray} from './apiarrays';


function Purchasesection(){
    const [first, setFirst] = useState(["Purchase Orders", "Purchase Entry", "Purchase Returns"]);
    const [second, setSecond] = useState(0);

    const _selectedScreen = (_selected) => {
        if (_selected === 0) {
          return <Purchaseordersection />
        }
        if (_selected === 1) {
          return <Purchaseentrysection />
        }
        if (_selected === 2) {
          return <div className="">{_selected}</div>
        }
        return <div className='fs-2'>Nothing Selected</div>
    
      }
return(
<>

  <section className='purchasesection pt-3'>
        <div className="container-fluid">
          <div className="row g-3">
            <div className="col-10">
              <div className='row'>
                {
                  first.map((e, i) => {
                    return (
                      <div className="col-auto">
                        <button className={`btn btn-sm px-4 rounded-5 text-${i === second ? "light" : "dark"} bg-${i === second ? "charcoal" : "seashell"}`} onClick={(a) => setSecond(i)} >{e}</button>
                      </div>
                    )
                  }
                  )
                }
              </div>
            </div>
       
          </div>
        </div>
      </section>
      <section className="tablesrender position-relative">
        <div className='container-fluid pt-5'>
          <div className="scroll scroll-y">
            {_selectedScreen(second)}
          </div>
        </div>
      </section>
</>
)
}

function Purchaseordersection(){
  const [cartselect, setcartselect] = useState(["Pharmacy", "Clinic"]);
  const [cartselectindex, setcartselectindex] = useState(0);

  const _selectedcart = (cardindex) => {
      if (_selected === 0) {
        return <Pharmacystocktable />
      }
      if (_selected === 1) {
        return <Clinicstocktable />
      }
      if (_selected === 2) {
        return <div className="">{_selected}</div>
      }
      return <div className='fs-2'>Nothing Selected</div>
  
    }
    return(    <>
     <button className="button viewcart button-charcoal position-absolute"><img src={process.env.PUBLIC_URL + "/images/cartwhite.png"} alt='displaying_image' className="img-fluid" style={{ width: `1.5rem` }}/>View Cart</button>
      <div className="container cartform w-50 border1 rounded bg-seashell position-absolute text-center" >
        <h4>Items in Cart</h4>
        <div className="pt-1">
          <div className="row g-0 justify-content-center">
            <button className="col-2 button button-charcoal rounded-0">Pharmacy</button>
            <button className="col-2 button button-seashell rounded-0">Clinic</button>
          </div>
        </div>
      </div>
    <h3 className='ps-3'>Purchase Order List</h3>
    <table className="table datatable text-center">
        <thead>
          <tr>
            <th>PO ID</th>
            <th>Channel</th>
            <th>Vendor</th>
            <th>PO Date</th>
            <th>Created By</th>
            <th>Total Items</th>
            <th>Amount</th>
            <th>Approval Status</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
        {<Purchaseorderarray/>}
        </tbody>
      </table>
    </>
    )
}
function Purchaseentrysection(){
    return(
        <>
        <button className="button addentrypurchase button-charcoal position-absolute"><img src={process.env.PUBLIC_URL + "/images/addiconwhite.png"} alt='displaying_image' className="img-fluid" style={{ width: `1.5rem` }}/>Entry Purchase</button>
         <h3 className='ps-3'>Purchase Entry List</h3>
        <table className="table datatable text-center">
        <thead>
          <tr>
            <th>PE ID</th>
            <th>PO ID</th>
            <th>Channel</th>
            <th>Invoice No.</th>
            <th>Bill Date</th>
            <th>Bill Total</th>
            <th>Vendor</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
            {<Purchaseentryarray/>}
        </tbody>
      </table></> 
    )
}

function Purchasestocktable(){
  return(
    <>
     <table className="table datatable text-center">
        <thead>
          <tr>
            <th>No.</th>
            <th>Item Name</th>
            <th>Total Qty</th>
            <th>Amount</th>
            <th>Last Vendor</th>
            <th>Add</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
            {<Purchaseentryarray/>}
        </tbody>
      </table>
    </>
  )
}
export{Purchasesection};
export{Purchaseordersection};
export{Purchaseentrysection};
