import React, { useState, useEffect, useContext } from 'react'
import { URL, Vitals } from '../../index'
import '../../css/bootstrap.css'
import '../../css/dashboard.css'
import axios from 'axios'
const Vitalsoperation = (props) => {
  const url = useContext(URL)
  const vitals = useContext(Vitals)
  console.log(vitals)
  const [vitalid, setvitalid] = useState()
function Vitalunit(vitalid){
  for (let x in vitals){
    if (vitals[x].id==vitalid){
      return vitals[x].unit
    }
  }
}
  function AddVitals() {
    axios.post(`url`)
  }

  return (
    <div className='bg-seashell rounded-2 position-relative'>
      <h4>{props.patientname} Vitals</h4>
      <button className=' btn-close position-absolute top-0 end-0 m-1 me-2' onClick={props.CloseVitals}></button>
      <div className="container-fluid">
        <div className="row">
          <div className="col-4">
            <select className='form-control bg-seashell' value={vitalid ? vitalid : ''} onChange={(e) => {setvitalid(e.target.value);Vitalunit(e.target.value) }}>
              <option>Select Vitals</option>
              {
                vitals.map((data) => (
                  <option value={data.id}>{data.title}</option>
                ))
              }
            </select>

          </div>
          <div className="col-3">
            <input className='form-control' />
          </div>
          <div className="col-3">
            <p>measurement</p>
            <h3 className='text-charcoal'>{Vitalunit()}</h3>


          </div>
        </div>
      </div>
    </div>
  )
}

export { Vitalsoperation }
