            <th>Created By</th>
            <th>Total Items</th>
            <th>Amount</th>
            <th>Approval Status</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {<Purchaseorderarray itemdetails={_displayitemdetails} />}
        </tbody>
      </table>
    </>
  )
}
