import React from "react";
import axios from "axios";
import { TodayDate } from '../../index';
import { useState, useEffect, useContext } from "react";
const url = 'https://aartas-qaapp-as.azurewebsites.net/aartas_uat/public/api/connect';
let array = [];
function DoctorSchedule(props) {

  const Date = useContext(TodayDate);
  function ArraySum(arr) {
    if (arr.length > 0) {
      let sum = arr.reduce((accumulator, value) => {
        return accumulator + value;
      }, 0);
      return sum;
    } else {
      return 0;
    }

  }
  const [appointmentdata, setappointmentdata] = useState([]);
  const [isLoading, setisLoading] = useState();
  const [payment, setpayment] = useState([]);

  let payments = [];
  const [ttlpay, setttlpay] = useState();
  async function Appointmentlist() {
    setisLoading(true);
    await axios.get(`${url}/appointment/list?doctor_id=${props.todayDoc[props._selected][0]}&from_date=${Date}&to_date=${Date}`).then((response) => {
      setappointmentdata(response.data.data);
      for (let i = 0; i < appointmentdata.length; i++) {
        payments.push(appointmentdata[i].payment_method_details != null ? Object.values(JSON.parse(appointmentdata[i].payment_method_details)) : 0);
        for (let j = 0; j < payments.length; j++) {
          if (payments[j].length > 0) {
            for (let k = 0; k < payments[j].length; k++) {
              payments[j][k] = parseFloat(payments[j][k]);
            }
          }
          ArraySum(payments[j]);
        }
        setpayment(payments);
      }
    }).then(function (){
       payment.map((data) =>(
        data != null ? (array.push(ArraySum(data))) : (array.push([0]))
      ))
    });
    setttlpay(array);
    console.log(ttlpay);
    setisLoading(false);

  }

  useEffect(() => {
    Appointmentlist();
  
  }, [props._selected]);
  setttlpay([]);
  function tConvert(time) {

    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) {
      time = time.slice(1);
      time[3] = +time[0] < 12 ? ' AM ' : ' PM ';
      time[0] = +time[0] % 12 || 12;
    }
    return time.join('');
  }

  return (
    <>
      <section id="doctorscheduledata">
        <section className="timeslotsection">
          <div className="container-fluid ">
            <div className="row ps-md-3 align-items-center ">
              <div className=" col-12 p-0 m-0 align-items-center">
                <div className="d-flex">
                  <div className="col-auto">
                    <h4 className="p-0 m-0">Time slot avaliable</h4>
                  </div>
                  <div className="col-2 ms-2">
                    <button className="btn m-0 p-0">
                      <img src={process.env.PUBLIC_URL + "/images/addicon.png"} style={{ width: "2rem" }} alt="displaying_image" />
                    </button>
                  </div>
                </div>
              </div>
            </div>
            <div className="scroll timeslots col-12 justify-content-around">
              {
                props.todayDoc[props._selected][2].map((data) => (
                  <button className="btn timeslotbtns m-1">{tConvert(data)}</button>
                ))

              }
            </div>
          </div>
        </section>

        <section className="allappointmentsection">
          <div className="container-fluid">
            {
              isLoading ? (<>
                <button className="btn btn-primary" type="button" disabled>
                  <span className="spinner-grow spinner-grow-sm" role="status" aria-hidden="true"></span>
                  <span className="visually-hidden">Loading...</span>
                </button>
                <button className="btn btn-primary" type="button" disabled>
                  <span className="spinner-grow spinner-grow-sm" role="status" aria-hidden="true"></span>
                  Loading...
                </button>
              </>) : (
                <div className="scroll">
                  <table className="table datatable text-center position-relative" style={{ minHeight: '8rem' }}>
                    <thead>
                      <tr>
                        <th>Action</th>
                        <th>Status</th>
                        <th>Patient Name</th>
                        <th>Phone Number</th>
                        <th>Time</th>
                        <th>Total Amount</th>
                        <th>Amount Status</th>
                        <th>Vitals</th>
                        <th>Actions</th>
                        <th>More</th>
                      </tr>
                    </thead>
                    {
                      appointmentdata.length == 0 ? (
                        <>

                          <h2 className="text-center position-absolute bottom-0 start-0 end-0">No Patients Found</h2>

                        </>) : (
                        appointmentdata.map((data, i) => (
                          <tbody>
                            <tr>
                              <th scope="row">
                                <button className="btn btn-lg px-1 action position-relative bg-transparent confirmed position-relative m-0 p-0">
                                  <img src={process.env.PUBLIC_URL + "/images/confirmed.png"} alt="displaying_image" className="img-fluid" style={{ width: "1.5rem" }} />
                                </button>

                                <table className="table p-2 actionoptions optionconfirmed text-start rounded position-absolute">
                                  <tbody>
                                    <tr className="bg-transparent"> <td>Action 1</td> </tr>
                                    <tr className="bg-transparent"> <td>Action 2</td> </tr>
                                    <tr className="bg-transparent"> <td>Action 3</td>
                                    </tr>
                                  </tbody>
                                </table>
                              </th>

                              <td>
                                <button className="btn btn-sm status_confirmed"> Confirmed </button>
                              </td>
                              <td>{data.patient.full_name}</td>
                              <td>{data.patient.phone_number}</td>
                              <td>{tConvert(data.timeslot.time_from)}</td>
                              <td>{data.total_amount}</td>
                              <td>
                                {
                                  ttlpay ? (
                                    ttlpay[i] == data.total_amount ? (<>
                                      <button className="ms-1 btn btn-sm status_completed">{ttlpay[i]}|Done</button>
                                    </>) : (<>
                                      <button className="ms-1 btn btn-sm status_pending">{ttlpay[i]}| Pending</button>
                                    </>)

                                  ) : (
                                    <button className="btn btn-primary" type="button" disabled>
                                      <span className="spinner-grow spinner-grow-sm" role="status" aria-hidden="true"></span>
                                    </button>
                                  )}
                              </td>
                              <td>
                                <img src={process.env.PUBLIC_URL + "/images/vitals.png"} alt="displaying_image" style={{ width: "1.5rem" }} />
                              </td>
                              <td>
                                <img src={process.env.PUBLIC_URL + "/images/cart.png"} alt="displaying_image" style={{ width: "1.5rem" }} className="me-1" />
                                <img src={process.env.PUBLIC_URL + "/images/medicine.png"} alt="displaying_image" className="ms-1" style={{ width: "1.5rem" }} />
                              </td>
                              <td>
                                <button className="btn position-relative cursor-pointer more p-0 m-0">
                                  <img
                                    src={process.env.PUBLIC_URL + "/images/more.png"}
                                    alt="displaying_image"
                                    style={{ width: "1.5rem" }}
                                  />
                                </button>
                                <table className="table p-2 moreoptions text-start rounded position-absolute">
                                  <tbody>
                                    <tr>
                                      <td>Action 1</td>
                                    </tr>
                                    <tr>
                                      <td>Action 2</td>
                                    </tr>
                                    <tr>
                                      <td>Action 3</td>
                                    </tr>
                                  </tbody>
                                </table>
                              </td>
                            </tr>
                          </tbody>

                        )))}
                  </table>
                </div>

              )
            }
          </div>
        </section>
      </section>
    </>
  );
}
export { DoctorSchedule };

function Timecard(props) {

  function diff(start, end) {
    start = start.split(":");
    end = end.split(":");
    var startDate = new Date(0, 0, 0, start[0], start[1], 0);
    var endDate = new Date(0, 0, 0, end[0], end[1], 0);
    var diff = endDate.getTime() - startDate.getTime();
    var hours = Math.floor(diff / 1000 / 60 / 60);
    diff -= hours * 1000 * 60 * 60;
    var minutes = Math.floor(diff / 1000 / 60);
    if (hours < 0)
      hours = hours + 24;

    return (hours <= 9 ? "0" : "") + hours + "hrs" + (minutes <= 9 ? "0" : "") + minutes + 'mins';
  }

  function tConvert(time) {

    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) {
      time = time.slice(1);
      time[3] = +time[0] < 12 ? ' AM ' : ' PM ';
      time[0] = +time[0] % 12 || 12;
    }
    return time.join('');
  }
  let [doctime, setdoctime] = useState([]);
  let [isLoading, setisLoading] = useState();
  let [doctorid, setdoctorid] = useState();
  let doctimes = [];
  let clinicid = 1;
  function fetchapi() {
    setisLoading(true);
    axios.get(`${url}/todays/doctor/time/list?doctor_id=${props.docid}&clinic_id=${clinicid}`).then((response) => {
      if (response.data.data.length == 0) {
        setisLoading(false);
      }
      else {
        response.data.data.map((data) => {
          doctimes.push(data);
          setdoctorid(props.docid);
          setdoctime(doctimes);
          setisLoading(false);
        })

      }
    })
  }
  const [refreshtimeslots, setrefreshtimeslot] = useState();

  async function endtimeslot(e) {
    let adminid = localStorage.getItem('id');
    setrefreshtimeslot(true);
    let log_id = e.target.value;
    await axios.post(`${url}/doctor/end/time`, {
      admin_id: adminid,
      log_id: log_id
    }).then((response) => {
      return response;
    })
    fetchapi();
    setrefreshtimeslot(false);
  }
  const [roomnumber, setroomnumber] = useState('1');
  async function starttimeslot() {
    let adminid = localStorage.getItem('id');
    let doctorid = JSON.stringify(props.docid);
    await axios.post(`${url}/doctor/start/time`, {
      clinic_id: clinicid,
      room_id: roomnumber,
      doctor_id: props.docid,
      admin_id: adminid,


    }).then((response) => {
      return response;
    })
    fetchapi();
    setrefreshtimeslot(false);
  }
  useEffect(() => {
    fetchapi();
  }, [props._selected]);

  const [displaytimecard, setdisplaytimecard] = useState('none');
  const [displaytimecardbtn, setdisplaytimecardbtn] = useState('inline-flex');
  const addnewtimecard = () => {
    if (displaytimecard === 'none') {
      setdisplaytimecard('inline-flex');
      setdisplaytimecardbtn('none');
    }
    if (displaytimecard === 'inline-flex') {
      setdisplaytimecard('none');
      setdisplaytimecardbtn('inline-flex');
    }
  }

  return (
    <div className="scroll align-items-center align-content-center my-auto mb-2">
      {
        isLoading ? (
          <div className="container-fliud pt-3">
            <div className="d-flex fs-2 align-items-center justify-content-around">
              <strong className="text-burntumber">Loading Timeslots...</strong>
              <div
                className="text-burntumber spinner-border ml-auto"
                role="status"
                aria-hidden="true"
              ></div>
            </div>
          </div>
        ) : (doctorid == props.docid ? (
          doctime.map(({ room_id, id, login_time, logout_time }) => (
            <div id="cardslot" className="d-inline-flex m-1">
              <div className="card p-0 m-0 text-center" id="card1">
                <div className="card-body p-0 m-0">
                  <div className="d-flex p-0 m-0">
                    <div className="col-8 p-0 m-0">
                      <p className=" p-0 m-0 text-end mt-1">Room No.</p>
                    </div>
                    <div className="col-auto p-0 m-0">
                      <select className="form-control mb-0 pb-0 bg-transparent text-center border-0" id="clinicroom">
                        <option defaultValue="01">{room_id}</option>
                      </select>
                    </div>
                  </div>
                  <div className="row justify-content-center m-0 p-0">
                    {
                      refreshtimeslots ? (
                        <div className="container-fliud pt-3">
                          <div className="d-flex fs-6 align-items-center justify-content-around">
                            <h6 className="text-burntumber">Updating...</h6>
                            <div className="text-burntumber spinner-border ml-auto" role="status" aria-hidden="true" ></div>
                          </div>
                        </div>
                      ) : (
                        <>
                          <div className="d-flex p-0 m-0 justify-content-center">
                            <div className="col-auto p-0 m-0">
                              <button type="text" className="btn p-0 m-0 float-end text-charcoal text-center">{(login_time) ? tConvert(login_time) : ''}</button>
                            </div>
                            <div className='col-auto'>-</div>
                            <div className="col-auto p-0 m-0">
                              <button type="text" className="btn p-0 m-0 text-charcoal float-end text-center">{(logout_time) ? tConvert(logout_time) : ''}</button>
                            </div>
                          </div>
                          <div className="col-12 p-0 m-0 text-center">
                            <button id="totalhrs" className="btn p-0 m-0 text-charcoal text-center" defaultValue="00">{logout_time ? diff(login_time, logout_time) + ' Used' : 'Click to get Details'}</button>

                          </div>

                          <div className="d-flex justify-content-around">
                            {
                              logout_time ? (
                                <button disabled className="btn m-0 mb-1 p-1 btn-danger" id="endbtn">Time Ended</button>
                              ) : (
                                <button className="btn btn-danger m-0 mb-1 p-1" id="endbtn" value={id} onClick={endtimeslot}>End Time</button>
                              )
                            }


                          </div>

                        </>
                      )
                    }
                  </div>


                </div>

              </div>
            </div>
          )
          )) : (
          <div></div>
        ))
      }

      <div id="cardslot" className={`d-${displaytimecard} m-1`}>
        <div className="card  card1 p-0 m-0 text-center" id="card1" >
          <div className="card-body p-0 m-0">
            <button type="button" className="btn-close closebtn float-end" aria-label="Close" onClick={addnewtimecard}></button>
            <div className="d-flex">
              <div className="col-8 p-0 m-0">
                <p className=" m-0 p-0 text-end mt-1">Room No.</p>
              </div>
              <div className="col-auto p-0 m-0">
                <select onChange={(e) => { setroomnumber(e.target.value) }} className="form-control mb-0 pb-0 bg-transparent text-center border-0" id="clinicroom1">
                  <option defaultValue="1">1</option>
                  <option defaultValue="2">2</option>
                  <option defaultValue="3">3</option>
                  <option defaultValue="4">4</option>
                  <option defaultValue="5">5</option>
                  <option defaultValue="6">6</option>
                  <option defaultValue="7">7</option>
                  <option defaultValue="8">8</option>
                  <option defaultValue="9">9</option>
                  <option defaultValue="10">10</option>
                  <option defaultValue="11">11</option>
                  <option defaultValue="12">12</option>
                  <option defaultValue="13">13</option>
                </select>
              </div>
            </div>
            <div className="d-flex justify-content-center">
              <div className="col-auto p-0 m-0"> <button type="text" className="btn m-0 p-0 float-end text-charcoal text-center">00:00</button> </div>
              <div className="col-auto">-</div>
              <div className="col-auto p-0 m-0"> <button type="text" className="btn m-0 p-0 float-end text-charcoal text-center">00:00</button> </div>
            </div>
            <div className="d-flex justify-content-center">
              <button id="totalhrs" className="btn m-0 p-0 text-charcoal text-center" defaultValue="00">Click to book the Room</button>
            </div>
            <div className="col-12">
              <div className="d-flex justify-content-around">
                <button className="btn m-0 mb-1 p-1 btn-success startbtn" onClick={starttimeslot}>Start Time</button>
              </div>
            </div>

          </div>

        </div>
      </div>
      <div className={`d-${displaytimecardbtn}`} id="addslotbutton">
        <button className='btn button-seashell ms-3' onClick={addnewtimecard}>
          <p className="text-burntumber">Click the button below to <br />add TimeCard</p>
          <img src={process.env.PUBLIC_URL + "/images/addicon.png"} alt="displaying_image" style={{ width: "3rem" }} />
        </button>
      </div>
    </div>

  )
}
export { Timecard }



