import React, { useState, useEffect, useContext } from 'react'
import { URL, Vitals } from '../../index'
import '../../css/bootstrap.css'
import '../../css/dashboard.css'
import axios from 'axios'
import Notiflix from 'notiflix'


const Vitalsoperation = (props) => {
  const url = useContext(URL)
  const vitals = useContext(Vitals)
  const [vitalid, setvitalid] = useState()
  const [ vitalvalue,setvitalvalue]=useState()

function Vitalunit(vitalid){
  let v; 
  for (let x in vitals){
    if (vitals[x].id == vitalid){
      v=  vitals[x].unit
    }
  }
  return v
}
async function AppointmentVitals(){
  await axios.get()
}
 async function AddVitals() {
  console.log(vitalid,vitalvalue,props.appointmentid,props.patientid)
    try{
      if(vitalid && vitalvalue && props.appointmentid && props.patientid ){
      await axios.post(`${url}/save/vitals`,{
          vitals_id:vitalid,
          value:vitalvalue,
          appointment_id:props.appointmentid,
          patient_id:props.patientid,
        }).then((response)=>{
          Notiflix.Notify.success(response.data.message)
        })
      }else{
        Notiflix.Notify.warning('Please Fill all Details')
      }
    }catch(e){
      Notiflix.Notify.alert(e.message)
    }
  }
  console.log(props.appointmentvitalslist)
  return (
    <div className='bg-seashell rounded-2 position-relative'>
      <h4>{props.patientname} Vitals</h4>
      <button className=' btn-close position-absolute top-0 end-0 m-1 me-2' onClick={props.CloseVitals}></button>
      {
        props.loadvitals ? (
          <div className="col-6 py-2 pb-2 m-auto text-center">
          <div class="spinner-border" role="status">
              <span class="visually-hidden">Loading...</span>
          </div>
          </div>
        ):(
          props.appointmentvitalslist.length == 0 ? (
            <div className='bg-lightred p-2 mb-2 text-center align-items-center '><h6 className='text-center text-charcoal-75 fw-bold mb-0'>No Vitals Added</h6></div>
           ):(
            props.appointmentvitalslist.map((data)=>(
              <div className="container-fluid">
              <div className="row justify">
                <div className="col-auto">
                  <select className='form-control p-0 border-0 text-center bg-seashell' value={data.vital.id  ? data.vital.id : vitalid ? vitalid : ''} onChange={(e) => {setvitalid(e.target.value) }}>
                    <option>Select Vitals</option>
                    {
                    data.vital.id ?(
                      <option value={data.vital.id}>{data.vital.title}</option>
                    ):(
                        vitals.map((data) => (
                          <option value={data.id}>{data.title}</option>
                        ))
                      
                    )
                 
                  }
                  </select>
      
                </div>
                <div className="col-4 text-center">
                  <div className="row">
                    <div className="col-8">
                    <input className='form-control bg-seashell p-0 border-bottom-burntumber'  />
                    </div>
                    <div className="col-auto p-0 m-0">
                    <p className='text-charcoal text-end'>{Vitalunit(vitalid)}</p>
                    </div>
                  </div>
      
                </div>
              </div>
                </div>
  
            ))
          )

        )
      }
   
      <button className='btn button-brandy text-burntumber fw-bold'>Click to Add Vital</button>
      <div className="container-fluid">
        <div className="row p-0 m-0 justify-content-center">
          <div className="col-3 p-0 m-0 pe-2">
            <select className='form-control p-0 border-0 text-end bg-seashell' value={vitalid ? vitalid : ''} onChange={(e) => {setvitalid(e.target.value) }}>
              <option>Select Vitals</option>
              {
                vitals.map((data) => (
                  <option value={data.id}>{data.title}</option>
                ))
              }
            </select>
          </div>
          <div className="col-2 p-0 m-0" style={{width:'20%'}}>
              <input className='form-control mx-1 bg-seashell text-center p-0 border-start-0 border-end-0 border-top-0 rounded-0 border-bottom-burntumber' value={vitalvalue ? vitalvalue:''} onChange={(e)=>{setvitalvalue(e.target.value)}} />
          </div>
          <div className="col-auto p-0 m-0">
              <p className='text-charcoal text-start'>{Vitalunit(vitalid)}</p>
          </div>
          <div className="col-3 p-0 m-0">
          <button className='btn py-0 button-burntumber' onClick={AddVitals}>Add</button>
        </div>
        </div>
        
      </div>
    </div>
  )
}

export { Vitalsoperation }
