  async function InsertMedicines() {
    MedicineentriesObj = {
      type: itemtype,
      Itemid: itemid,
      Itemname: itemname,
      batchno: batchno,
      expirydate: expdate,
      manufacturingDate: manufdate,
      MRP: mrp,
