          batchno: batchno,
          expirydate: expdate,
          manufacturingDate: manufdate,
          MRP: mrp,
          Rate: rate,
          Qty: qty,
          freeQty: freeqty,
          Discount: disc,
          tradeDiscount: trddisc,
          sgstper: sgstprcnt,
          sgst: sgst,
          cgstper: cgstprcnt,
          cgst: cgst,
          igstper: igstprcnt,
          igst: igst,
          costperunit: cpu,
          totalamount: totalamt
        }
        Notiflix.Notify.success(`Item Number ${tableindex + 1} Updated Successfully`)
        Emptytableindex()
        settableindex(-1)
      }
    }
  }
  async function DeleteMedicine(id) {
    let obj = []
    obj.push(MedicineentriesArr.filter(function (e) {
      return e.Itemid !== id
    }))
    obj = obj.flat()
    setMedicineentriesArr(obj)
  }
  
function Calculate(){
    if(rate && qty){
      settotalamt(rate*qty)
      if(freeqty){
        let e =totalamt-(rate*freeqty)
        settotalamt(e)
      }
    if(disc && !sgstprcnt && !cgstprcnt){
      let e = totalamt-((totalamt*disc)/100)
      e=parseFloat(e).toFixed(2)
      settotalamt(e)
    }
    if(trddisc && !sgstprcnt && !cgstprcnt){
      let e = totalamt-((totalamt*trddisc)/100)
      e=parseFloat(e).toFixed(2)
      settotalamt(e)
    }
    if(disc && trddisc && !sgstprcnt && !cgstprcnt){
      let e = totalamt-((totalamt*disc)/100)
      let ee = e-((e*trddisc)/100)
      ee=parseFloat(ee).toFixed(2)
      settotalamt(ee)
    }
    if(sgstprcnt){
      if(disc){
        let e = totalamt-((totalamt*disc)/100)
        e = (e+(e*sgstprcnt)/100)
        e=parseFloat(e).toFixed(2)
        settotalamt(e)
      }
      if(trddisc){
        let e = totalamt-((totalamt*trddisc)/100)
        e = (e+(e*sgstprcnt)/100)
        e=parseFloat(e).toFixed(2)
        settotalamt(e)
      }
      if(disc && trddisc){
        let e = totalamt-((totalamt*disc)/100)
        e = totalamt-((totalamt*trddisc)/100)
        e = e+((e*sgstprcnt)/100)
        e=parseFloat(e).toFixed(2)
        settotalamt(e)
      }

    }
  }else{
    settotalamt(rate?Number(rate):0)
  }

}
useEffect(()=>{
  Calculate()
},[rate,qty,disc,trddisc,sgstprcnt])
