import React, { useState, useEffect, useContext } from 'react'
import { URL, Vitals } from '../../index'
import '../../css/bootstrap.css'
import '../../css/dashboard.css'
import axios from 'axios'
const Vitalsoperation = (props) => {
  const url = useContext(URL)
  const vitals = useContext(Vitals)
  console.log(vitals)
  const [vitalid, setvitalid] = useState()
function Vitalunit(vitalid){
  let v; 
  for (let x in vitals){
    if (vitals[x].id == vitalid){
      console.log(vitals[x].unit)
      v=  vitals[x].unit

    }
  }
  return v
}
  function AddVitals() {
    axios.post(`url`)
  }

  return (
    <div className='bg-seashell rounded-2 position-relative'>
      <h4>{props.patientname} Vitals</h4>
      <button className=' btn-close position-absolute top-0 end-0 m-1 me-2' onClick={props.CloseVitals}></button>
      <div className='bg-lightred p-2 mb-2 text-center align-items-center'><h6 className='text-center'>No Vitals Added</h6></div>
      <button className='btn button-brandy text-burntumber fw-bold'>Click to Add Vital</button>
      <div className="container-fluid">
        <div className="row">
          <div className="col-auto">
            <select className='form-control p-0 border-0 text-center bg-seashell' value={vitalid ? vitalid : ''} onChange={(e) => {setvitalid(e.target.value) }}>
              <option>Select Vitals</option>
              {
                vitals.map((data) => (
                  <option value={data.id}>{data.title}</option>
                ))
              }
            </select>

          </div>
          <div className="col-4">
            <div className="row">
              <div className="col-8">
              <input className='form-control bg-seashell p-0 border-bottom-burntumber' />
              </div>
              <div className="col-auto p-0 m-0">
              <p className='text-charcoal text-end'>{Vitalunit(vitalid)}</p>
              </div>
            </div>

          </div>
        </div>
      </div>
    </div>
  )
}

export { Vitalsoperation }
