      <div className='d-flex justify-content-center p-0 m-0 mt-3 mb-1'>
        {
          Items.map((data, i) => (

            <button className={`button border-charcoal rounded-0 shadow-0 button-${i == index ? 'charcoal' : 'seashell'}`} onClick={() => { setindex(i) }}>{data}</button>

          ))
