                        </div>
                      ) : (
                        itemsearch.length == 0 ? (
                          <div className="bg-burntumber text-light rounded-2 p-1">Oops! Not Avaliable</div>
                        ) : (
                          itemsearch.map((data, i) => (
                            <div style={{ cursor: 'pointer' }} className={`p-0 ps-1 shadow bg-${((i % 2) == 0) ? 'pearl' : 'lightyellow'} fs-6 `} name={data.id}
