import React,{useState,useEffect} from 'react'
const Timer=()=>{
    const [time, setTime] = useState(new Date());
    useEffect(() => {
        setInterval(() => {
            setTime(new Date());
        }, 1000);
    }, []);

    return (
        <span>
            {time.toLocaleString("en-US", {
                timeStyle: "medium",
                hour12: false,
            })}{" "}
        </span>
    );
}



export {Timer}
