import React from 'react'
import { useState } from 'react'

const PendingPayments = (props) => {
    const [ blocksindex,setblocksindex]=useState(0)
    const blocks = ['Advance payments','Pending Payments']
  return (
    <div className='bg-seashell p-2 rounded-2'>
       <h4 className='fs-4'>Payments Section</h4> 
       <button className='btn-close position-absolute end-0 p-2 top-0' onClick={props.ClosePendingPayments}></button>
       <div className="container">
        <div className="row">
            {
                blocks.map((Data,i)=>(
                    <div className="col-3 ms-3">
                    <button className={`button button-${i===blocksindex ? 'charcoal':'seashell'} shadow`} onClick={()=>{setblocksindex(i)}}>{Data}</button>
                    </div>
                ))
            }
           
        </div>
        <div className="container-fluid textx-start mt-2">
            {
                blocks.map((data,i)=>(
                    <>
                    <div className={`container d-${blocksindex===0?'block':'none'}`}>
                        <h6>Advance Payment from {props.patientname}</h6>
                    </div>
                      <div className={`container d-${blocksindex===1?'block':'none'}`}>
                      <h6>Pending Payment from {props.patientname}</h6>
                      </div>
                      </>
                ))
            }
      
        </div>
       </div>

    </div>
  )
}

export  {PendingPayments}
