import React from 'react';

function Timecard() {
    return (
        <div className="card m-2 ps-md-3 cardcss card1" id="card1">
            <div className="card-body p-2">
                <button type="button" className="btn-close closebtn float-end" aria-label="Close"></button>
                <div className="row"><div className="col-8">
                    <h5 className="card-title fw-bolder text-center mt-1">Room Number</h5>
                </div>
                    <div className="col-4">
                        <select className="form-control bg-transparent border-0 " id="clinicroom1">
                            <option defaultValue="01">01</option>
                            <option defaultValue="02">02</option>
                            <option defaultValue="03">03</option>
                            <option defaultValue="04">04</option>
                            <option defaultValue="05">05</option>
                            <option defaultValue="06">06</option>
                            <option defaultValue="07">07</option>
                            <option defaultValue="08">08</option>
                            <option defaultValue="09">09</option>
                            <option defaultValue="10">10</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col-6">
                        <p className="card-text text-center mt-2">Start Time</p>
                    </div>
                    <div className="col-6">
                        <input type="time" className="form-control starttime bg-transparent border-0 text-center" defaultValue="00:00:00" />
                    </div>
                </div>
                <div className="row">
                    <div className="col-6">
                        <p className="card-text text-center mt-2">End Time</p>
                    </div><div className="col-6">
                        <input type="time" id="endtime" className="form-control bg-transparent border-0" defaultValue="00:00:00" />
                    </div>
                </div>
                <div className="row">
                    <div className="col-6">
                        <p className="card-text text-center mt-2" >Total Hours</p>
                    </div>
                    <div className="col-6">
                        <input type="text" id="totalhrs" className="form-control bg-transparent border-0 text-center" defaultValue="00" />
                    </div>
                </div>
                <div className="col-12">
                    <div className="row justify-content-around">
                        <button  className="btn btn-success col-5 startbtn">Start Time</button>
                        <button className="btn btn-danger col-5" id="endbtn">End Time</button>
                    </div>
                </div>
            </div>
        </div>
    )

}

export{Timecard}



