import React from 'react'
import { useState, useEffect, useContext, useRef } from "react"
import axios from "axios"
import AmountPaid from '../Medical/AmountPaid'
import { URL } from '../../index'
import '../../../node_modules/bootstrap/js/dist/dropdown'
import { UpdateAppointment } from './UpdateAppointment'
import '../../css/appointment.css'
import '../../css/bootstrap.css'



const AllAppointmentslist = (props) => {
    //  let idref = useRef()
    //     let id = idref.current.id; 
    const url = useContext(URL)
    const [appointmentform, setappointmentform] = useState("none");
    const [d_form, setd_form] = useState()

    const closeappointmentform = () => {

        if (appointmentform === "block") {
            setappointmentform("none");
            setd_form()
        }
    };
    const openapppointmentform = () => {
        if (appointmentform === "none") {
            setappointmentform("block");
            setd_form(true)
        }
    }

    const [getAppointments, setgetAppointments] = useState([])
    const [isLoading, setisLoading] = useState()

    async function fetchallAppointmentslist() {
        setisLoading(true)
        await axios.get(`${url}/appointment/list?${props.fromdate ? `?from_date=${props.fromdate}` : ''}&to_date${props.todate}&status=${props.type}`).then((response) => {
            setgetAppointments(response.data.data)
        })
        setisLoading(false)
    }

    useEffect(() => {
        fetchallAppointmentslist()
    }, [])

    useEffect(() => {
        fetchallAppointmentslist()
        console.log(`${url}/appointment/list?${props.fromdate ? `from_date${props.fromdate}` : null}&to_date${props.todate}&status=${props.type}`)
    }, [props.type, props.fromdate, props.todate])


    const [tableindex, settableindex] = useState()
    return (
        <>
            {
                isLoading == true ? (

                    <div className='container position-absolute start-0 end-0'>
                        <h4>Hold on its loading</h4>
                        <div className="spinner-grow bg-danger col-2" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </div>
                    </div>
                ) : (getAppointments.length == 0 ? (

                    <div className="container text-charcoal fs-4 position-absolute start-0 end-0">
                        <p className="p-0 m-0 text-charcoal75 fw-bolder mt-3">No Appointments found for the selected Date & Doctor</p>
                    </div>

                ) : (

                    getAppointments.map((data, key) => (
                        <tr id={key} className='position-relative'>
                            <th scope="row">
                                {/* <div class="btn-group">
                                <img src={process.env.PUBLIC_URL + "/images/confirmed.png"} style={{ width: "1.5rem" }} onClick={(e)=>{openapppointmentform();settableindex(key)}} className="btn p-0 m-0 dropdown-toggle" type="button" data-bs-toggle="dropdown" data-bs-auto-close="inside" aria-expanded="false" />
                                <ul class="dropdown-menu p-0 start-0  end-0 bg-transparent border-0 shadow-none m-0">
                                    <li><a class="dropdown-item" href="#" >Update {data.patient.full_name} Appointment Details</a></li>
                                    <li className={`d-${appointmentform} position-absolute start-0 top-0 end-0 bottom-5 `} style={{ width:'40rem'}}  key={key} ></li>
                                    <li><a class="dropdown-item" href="#">another option</a></li>
                                </ul>
                            </div> */}
                                <img src={process.env.PUBLIC_URL + "/images/confirmed.png"} style={{ width: "1.5rem" }} onClick={(e) => { openapppointmentform(); settableindex(key) }} className="btn p-0 m-0" />
                            </th>
                            <td><button className={`btn button-${props.status_color(data.appointment_status)} text-charcoal fw-bold rounded-5`}>{props.status(data.appointment_status)}</button></td>
                            <td>{data.patient.full_name}</td>
                            <td>{data.doctor.doctor_name}</td>
                            <td>{data.patient.phone_number}</td>
                            <td>{data.timeslot.date}</td>
                            <td>{props.tConvert(data.timeslot.time_from)}</td>
                            <td>{data.total_amount}</td>
                            <td><AmountPaid appointmentData={data} /></td>
                            <td><img src={process.env.PUBLIC_URL + "/images/vitals.png"} alt="displaying_image" style={{ width: "1.5rem" }} className='m-0 p-0' /> </td>
                            <td>{data.follow_up_date ? data.follow_up_date : ''}</td>
                            <td> <img src={process.env.PUBLIC_URL + "/images/cart.png"} alt="displaying_image" style={{ width: "1.5rem" }} className="me-1 m-0 p-0" /> </td>
                            <td>
                                <button className="btn position-relative cursor-pointer more p-0 m-0">
                                    <img src={process.env.PUBLIC_URL + "/images/more.png"} alt="displaying_image" style={{ width: "1.5rem" }} />
                                </button>
                                <table className="table p-2 moreoptions text-start rounded position-absolute" style={{ width: "min-content" }} >
                                    <tbody>
                                        <tr className="bg-transparent"> <td>Action 1</td> </tr>
                                        <tr className="bg-transparent"> <td>Action 2</td> </tr>
                                        <tr className="bg-transparent"> <td>Action 3</td> </tr>
                                    </tbody>
                                </table>
                            </td>
                            <td className={`d-${tableindex == key ? appointmentform : 'none'} start-0 end-0  position-absolute`} style={{ zIndex: '3005' }}><UpdateAppointment fetchallAppointmentslist={fetchallAppointmentslist} patientname={data.patient.full_name} patientid={data.patient.id} appointmentid={data.id} closeappointmentform={closeappointmentform} doctorid={props.doctorid} /></td>
                        </tr>
                    ))
                )
                )
            }
        </>
    )
}

export { AllAppointmentslist }
