    // }
    return (
        <>
            <h5 className="text-center mt-2">New Appointment</h5>
            {
                props.closeAddAppointmentform ? (
                    <button type="button" className="btn-close closebtn position-absolute" aria-label="Close" onClick={(e) => { props.closeAddAppointmentform() }} ></button>
                ) : (
                    <button type="button" className="btn-close closebtn position-absolute" aria-label="Close" onClick={(e) => { props.toggleappointmentform(); }} ></button>
                )
            }




            <hr />
            <div className="col-12">
                <label className="m-0 mb-2">Search Using Phone or Name</label>
                <input type="text" className="form-control selectpatient col-10 position-relative" value={searchinput ? searchinput : ''} onChange={searchpatient} onBlur={searchpatient} />
                <div className={`col-8 d-${displaysearchlist} searchinput`}>
                    {
                        searchlist.map((data) => (
                            <button className='col-12 p-0 m-0 border-0 bg-pearl text-charcoal text-start border border-1 shadow' name={data.id} value={data.full_name} onClick={get_value}>{data.full_name}  {data.phone_number}</button>
                        ))
                    }
                </div>
                {
                    searchlist && searchlist.length < 1 ? (
                        <p className="text-danger btn fs-6 p-0 m-0">Patient not found. Add as new User to book an Appointment</p>
                    ) : (<p className="text-danger btn fs-6 p-0 m-0"></p>)
                }

                <div className="col-12 p-0">
                    <a href="/#" className="btn text-decoration-none btn-sm done" onClick={props.formshift}> Add User </a>
                </div>
                <hr />
                <label>Select Location</label>
                <div className="col-12 form-control location  border-0" >
                    {
                        cliniclist.map((data, i) => (
                            <>
                                <label><input type="checkbox" className="radio form me-1" checked={ischecked == i ? true : false} name={data.id} onClick={(e) => { setclinicid(e.target.name); setischecked(i); }} /> {data.title} {data.address}</label>
                                <br /></>
                        ))
                    }
                </div>
                <hr />
                <div className="row p-0 m-0">
                    <div className="col-md-6">
                        <label>Selected Doctor</label>
                        <div className="col-12">
                            <select className="col-10 form-control selectdoctor" seec value={props.DoctorID} onClick={(e)=>console.log(e.target.value)}>
                                {
                                    Doclist.map((data) => (
                                        <option className='text-charcoal' value={data[0]}>{data[0]}.{data[1]} </option>
                                    ))
                                }
                            </select>

                        </div>
                    </div>
                    <div className="col-md-6">
                        <label className="">Selected Date</label>
                        <div className="col-12"><input type="date"  value={time ? time : APIDate}  className="form-control selectdate" />
                        </div>
                    </div>
                </div>

                <hr />

                <p className="m-0 mb-2">Select Time Slot</p>
                <div className="scroll align-items-center justify-content-around col-12">
                    {props.timeslots ? (
                        <>
                            {
                                props.timeslots.map((data, i) => (
                                    data[1] == 0 ? (
                                        <button className={`button button-${timeindex ? timeindex == i ? 'pearl' : 'lightgreen' : props.timeindex == i ? 'pearl':'lightgreen'} m-1`} value={data[2]} key={i}>{tConvert(data[0])}</button>
                                    ) : (
                                        <button disabled className='btn button-burntumber m-1' key={i} value={data[2]}>{tConvert(data[1])}</button>
                                    )

                                ))
                            }
                            <button className="btn btn-sm done m-1">
                                <img src="/images/addicon.png" alt="displaying_image" className="mb-1 me-1" style={{ width: "1.2rem" }} /> Time Slot
                            </button>

                        </>
                    ) : (
                        <div className='p-2 rounded'>Choose Doctor and Date to get Time Slots</div>
                    )
                    }
                </div>
                <hr />
                <div className="col-6 py-2 pb-2 m-auto text-center">
                    <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop2" className="btn done px-5" onClick={BookAppointment}> Done </button>
                </div>
                <div className="col-6 pb-2 m-auto text-center">
                    <button className="btn btn-light px-5">Reset</button>
                </div>
            </div>

            <div class="modal fade" id="staticBackdrop2" data-bs-backdrop="static" data-bs-keyboard="false" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h1 class="modal-title fs-5" id="staticBackdropLabel">Modal title</h1>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                            Do you Want to Add Appointment for {searchinput}
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" value='false' data-bs-dismiss="modal" onClick={BookAppointment}>Close</button>
                            <button type="button" class="btn btn-primary" value='true' data-bs-dismiss="modal" onClick={BookAppointment}>Proceed</button>
                        </div>
                    </div>
                </div>
            </div>
        </>

    )
}
export { SelectedTimeAppointment }
