import React from 'react'
import { useState, useEffect, useContext } from "react"
import axios from "axios"
import Notiflix from 'notiflix'

//Context APIs
import {URL,Apidate} from '../../index'

const AddDoctorSlot = (props) => {
    const url = useContext(URL)
    const [adddoctorfortoday, setadddoctorfortoday] = useState();
    const [docdate, setdocdate] = useState();
    const [fromtime, setfromtime] = useState();
    const [totime, settotime] = useState();
    const [clinicid, setclinicid] = useState();
    let admin_id = localStorage.getItem('id');
    async function AddDoctorForToday(e) {
        if(e.target.value == 'true'){
      if(adddoctorfortoday&&docdate&&fromtime&&totime&&clinicid&&admin_id){
      await axios.post(`${url}/doctor/add/timeslots`, {
        doctor_id: adddoctorfortoday,
        date: docdate,
        time_from: fromtime,
        time_to: totime,
        clinic_id: clinicid,
        admin_id: admin_id
      }).then((response) => {
        toggledoctorform();
        props.fetchapi();
        resetform();
        Notiflix.Notify.success(response.data.message);
      })
    }else{
      Notiflix.Notify.warning('Please Fill all Detais');
    }
  }
    }

  const [doctorform, setdoctorform] = useState("none");
  const toggledoctorform = () => {
    if (doctorform === "none") {
      setdoctorform("block");
    } else if (doctorform === "block") {
      setdoctorform("none");
    }
  };

console.log(props.docapi)
  const resetform=()=>{
    setadddoctorfortoday()
    setclinicid()
    setdocdate()
    setfromtime()
    settotime()
  }
  

 
  return (
    <>
    <h5 className="text-center mt-2">Slot Details</h5>
    <button type="button" className="btn-close closebtn position-absolute" aria-label="Close" onClick={props.toggledoctorform} ></button>
    <hr />
    <label>Doctor</label>
    <div className="col-12">
      <select className="col-10 form-control selectdoctor"  value={adddoctorfortoday ? adddoctorfortoday: ''}  onChange={(e) => {setadddoctorfortoday(e.target.value) }}>
        <option defaultValue="Select Doctor">Select Doctor</option>
        {props.docapi.length >0  ? (
     props.docapi.map((data) => (
        <option value={data[0]}>{data[0]}.{data[1]}</option>
      ))
        ):(
            <div>Loading...</div>
        )
     
        }
      </select>
    </div>
    <hr />
    <label>Clinic</label>
    <div className="col-12">
      <select className="col-10 form-control selectdoctor" value={clinicid ? clinicid :''}  onChange={(e) => { setclinicid(e.target.value) }}>
        <option defaultValue="Select Doctor">Select Clinic</option>
        <option value='1'>1.Aartas Delhi</option>
      </select>
    </div>
    <hr />
    <label>Date</label>
    <div className="col-12"><input type="date" className="form-control col-10" value={docdate ? docdate :''} onChange={(e) => { console.log(setdocdate(e.target.value)) }} /></div>
    <hr />
    <label>Time From</label>
    <div className="col-12"><input type="time" className="form-control col-10" value={fromtime ? fromtime :''} onChange={(e) => { console.log(setfromtime(e.target.value)) }} /></div>
    <hr />
    <label>Time To</label>
    <div className="col-12"><input type="time" className="form-control col-10" value={totime ? totime :''} onChange={(e) => { console.log(settotime(e.target.value)) }} /></div>
    <hr />
    <div className="col-6 py-2 pb-2 m-auto text-center">
    <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop3" className="btn done px-5" onClick={AddDoctorForToday} > Done </button>
            </div>
            <div className="col-6 pb-2 m-auto text-center">
                <button className="btn btn-light px-5" onClick={resetform}>Reset</button>
            </div>
            <div class="modal fade position-absolute" id="staticBackdrop3" data-bs-backdrop="static" data-bs-keyboard="false" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h1 class="modal-title fs-5" id="staticBackdropLabel">Add Doctor</h1>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body"> Do you Want to Add the following Doctor </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" value='false' data-bs-dismiss="modal" onClick={AddDoctorForToday} >Close</button>
                            <button type="button" class="btn btn-primary" value='true' data-bs-dismiss="modal" onClick={AddDoctorForToday}>Proceed</button>
                        </div>
                    </div>
                </div>
            </div>
            </>
  )
}

export  {AddDoctorSlot}
