import axios from 'axios'
import React from 'react'
import { URL } from '../../index'
import { useContext } from 'react'
import { useState,useEffect } from 'react'
import Notiflix from 'notiflix'

const PendingPayments = (props) => {
    const url= useContext(URL)
    const [ blocksindex,setblocksindex]=useState(0)
    const blocks = ['Advance payments','Pending Payments']
    const [advancepaymentmethods, setadvancepaymentmethods] = useState([])
    const [pendingpaymentmethods,setpendingpaymentmethods ]=useState([])
    const[loadadvancepayments,setloadadvancepayments] =useState(false)
    const [advancepayments,setadvancepayments]=useState([])
    const advancepaymentmethoddetails = {
        paymentmethod: '',
        amount: 0
    }
    const pendingpaymentmethodsddetails={
        paymentmethod: '',
        amount: 0
    }
    async function AdvancePayments(){
        setloadadvancepayments(true)
        axios.post(`${url}//advance/balance`,{
            patient_id:props.patientid
        }).then((response)=>{
            setloadadvancepayments(false)
            console.log(response)
        })
    } 
    useEffect(() => {
        AdvancePayments()
    }, [props.patientid])
    
    async function AddadvancePaymentMethods() {
        let Payments = []
        let amounts = []
        let allamounts = []
        for (let i = 0; i < props.appointmentdata.length; i++) {
            if (props.appointmentid == props.appointmentdata[i].id && props.appointmentdata[i].payment_method_details) {
                Payments.push(Object.keys(JSON.parse(props.appointmentdata[i].payment_method_details)))
                amounts.push(Object.values(JSON.parse(props.appointmentdata[i].payment_method_details)))
                
            }
        }
        let paymentobj = []
        let p = {
            paymentmethod: '',
            amount: 0
        }
        if (Payments[0]) {
            for (let j = 0; j < Payments[0].length; j++){
                allamounts.push( p={ paymentmethod: Payments[0][j], amount: amounts[0][j] })
               
            }
            setadvancepaymentmethods(allamounts)
        }
     
        // console.log(advancepaymentmethods)
        // paymentmethods.push(paymentobj)
    }
    async function AddpendingPaymentMethods() {
        let Payments = []
        let amounts = []
        let allamounts = []
        for (let i = 0; i < props.appointmentdata.length; i++) {
            if (props.appointmentid == props.appointmentdata[i].id && props.appointmentdata[i].pending_payments) {
                Payments.push(Object.keys(JSON.parse(props.appointmentdata[i].payment_method_details)))
                amounts.push(Object.values(JSON.parse(props.appointmentdata[i].payment_method_details)))
                
            }
        }
        let paymentobj = []
        let p = {
            paymentmethod: '',
            amount: 0
        }
        if (Payments[0]) {
            for (let j = 0; j < Payments[0].length; j++){
                allamounts.push( p={ paymentmethod: Payments[0][j], amount: amounts[0][j] })
               
            }
            setadvancepaymentmethods(allamounts)
        }
     
        // console.log(advancepaymentmethods)
        // paymentmethods.push(paymentobj)
    }
    useEffect(() => {
        AddadvancePaymentMethods()
        AddpendingPaymentMethods()
    }, [])
    function DeleteadvancePaymentMethods(i) {
        advancepaymentmethods.splice(i, i)
    }
    function DeletependingPaymentMethods(i){
        pendingpaymentmethods.splice(i,i)
