    let cgst = []
    let cgstpercent = []
    let igst = []
    let igstpercent = []
    let costperunit = []
    let totalamount = []
    let quantity = []
    let freequantity = []
    for (let i = 0; i < MedicineentriesArr.length; i++) {
      Type.push(MedicineentriesArr[i].type ? MedicineentriesArr[i].type : '')
      MedId.push(MedicineentriesArr[i].Medicineid ? MedicineentriesArr[i].Medicineid : '')
      medname.push(MedicineentriesArr[i].Medicinename ? MedicineentriesArr[i].Medicinename : '')
      batches.push(MedicineentriesArr[i].batchno ? MedicineentriesArr[i].batchno : '')
      expirydate.push(MedicineentriesArr[i].expirydate ? MedicineentriesArr[i].expirydate : '')
      manufacturingDate.push(MedicineentriesArr[i].manufacturingDate ? MedicineentriesArr[i].manufacturingDate : '')
      MRP.push(MedicineentriesArr[i].MRP ? MedicineentriesArr[i].MRP : '')
      Rate.push(MedicineentriesArr[i].Rate ? MedicineentriesArr[i].Rate : '')
      Discount.push(MedicineentriesArr[i].Discount ? MedicineentriesArr[i].Discount : '')
      tradeDiscount.push(MedicineentriesArr[i].tradeDiscount ? MedicineentriesArr[i].tradeDiscount : '')
      sgst.push(MedicineentriesArr[i].sgst ? MedicineentriesArr[i].sgst : '')
      sgstpercent.push(MedicineentriesArr[i].sgstpercent ? MedicineentriesArr[i].sgstpercent : '')
      cgst.push(MedicineentriesArr[i].cgst ? MedicineentriesArr[i].cgst : '')
      cgstpercent.push(MedicineentriesArr[i].cgstpercent ? MedicineentriesArr[i].cgstpercent : '')
      igst.push(MedicineentriesArr[i].igst ? MedicineentriesArr[i].igst : '')
      igstpercent.push(MedicineentriesArr[i].igstpercent ? MedicineentriesArr[i].igstpercent : '')
      costperunit.push(MedicineentriesArr[i].costperunit ? MedicineentriesArr[i].costperunit : '')
      totalamount.push(MedicineentriesArr[i].totalamount ? MedicineentriesArr[i].totalamount : '')
      quantity.push(MedicineentriesArr[i].quantity ? MedicineentriesArr[i].quantity : '')
      freequantity.push(MedicineentriesArr[i].freeqty ? MedicineentriesArr[i].freeqty : 0)

    }
    console.log(Type, MedId, medname, batches, expirydate, manufacturingDate, MRP, Rate, Discount, tradeDiscount, sgst, sgstpercent, cgst, cgstpercent, igst, igstpercent, costperunit, totalamount, quantity, freequantity)
    var Data = {
      distributor_id: vendorid,
      purchase_order_id: po,
      invoice_no: invoice,
      bill_date: invoicedate,
      clinic_id: ClinicId,
      channel: channel,
      bill_total: 435,
      id: MedId,
      type: Type,
      qty: quantity,
      free_qty: freequantity,
      mrp: MRP,
      rate: Rate,
      trade_discount: tradeDiscount,
      discount: Discount,
      SGST_rate: sgstpercent,
      SGST: sgst,
      CGST_rate: cgstpercent,
      CGST: cgst,
      IGST_rate: igstpercent,
      IGST: igst,
      cost: costperunit,
      total_amount: totalamount,
      expiry_date: expirydate,
      mfd_date: manufacturingDate,
      batch_no: batches,
    }

    try {
      await axios.post(`${url}/purchase/entry/save`, Data).then((response) => {
        Notiflix.Notify.success(response.data.message)
      })
    } catch (e) {
      Notiflix.Notify.warning(e.data)
      console.log(e)
    }

  }
