    setmedicineid()
    setbatchno()
    setexpdate()
    setmanufdate()
    setmrp()
    setrate()
    setqty()
    setfreeqty()
    setdisc()
    settrddisc()
    setcgst()
    setsgst()
    setigst()
    setcgstprcnt()
    setsgstprcnt()
    setigstprcnt()
    setcpu()
    settotalamt()
