      `Do you surely want to add total ${entries.length} purchase ${entries.length <= 1 ? 'entry' : 'entries'} of Distributor ${vendor}  `,
      'Yes',
      'No',
      () => {
        SavePurchase()
      },
      () => {
        return 0
      },
      {
      },
    );
  }
