  function Emptytableindex() {

    setmedicineid()
    setmedicinename()
    setbatchno()
    setexpdate()
    setmanufdate()
    setmrp()
    setrate()
    setqty()
    setfreeqty()
    setdisc()
    settrddisc()
    setcgst()
    setcgstprcnt()
    setsgst()
    setsgstprcnt()
    setigst()
    setigstprcnt()
    setcpu()
    settotalamt()

