  function UpdateMedicines() {
    for (let j = 0; j < MedicineentriesArr.length; j++) {
      if (tableindex == j) {
        MedicineentriesArr[j] = {
          type: 'm',
          Medicineid: medicineid,
          Medicinename: medicinename,
