                    <div className="col-5">
                      <label className="mb-2">Cost/Unit</label>
                      <input type="number" max="10" className="form-control bg-seashell costunit rounded-1" placeholder="00" value={cpu ? cpu : ''} onChange={(e) => { setcpu(e.target.value) }} required />
                    </div>
                    <div className="col-5">
                      <label className="mb-2">Total Amount</label>
                      <input type="number" max="10" className="form-control bg-seashell totalamount rounded-1" placeholder="00" value={totalamt ? totalamt : ''} onChange={(e) => { settotalamt(e.target.value) }} required />
                    </div>
                  </div>
                </div>

                <div className="col-6 py-3 m-auto text-center">
                  {
                    tableindex == -1 || tableindex == undefined ? (
                      <button type="submit" className="btn  button-charcoal done px-5" onClick={InsertMedicines} > Add </button>
                    ) : (
                      <button type="submit" className="btn  button-charcoal done px-5" onClick={UpdateMedicines} > Update </button>
                    )
                  }

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export { Navbar };
export { Doctorsection };
export { Appointments };
export { Pharmacy };
export { Patients };
export { Doctors };
export { DailySaleReport };
