import Draggable from "react-draggable"
import { Link } from "react-router-dom"
import { useState, useEffect, useContext } from "react"
import axios from "axios"
//Context APIs
import { URL, TodayDate, DoctorsList } from '../src/index'

//Components
import { DoctorSchedule, Timecard } from "./components/Medical/Doctor"
import { Purchasesection, Stocksection, PEitemdetailssection, } from "./components/pharmacy/pharmacy"
import { Livetime } from "./components/features/livetime"
import { Newpurchaseentryarray } from "./components/pharmacy/apiarrays"
import { AddAppointment } from './components/Medical/AddAppointment'
import { AddPatient } from './components/Medical/AddPatient'
import { AddDoctorSlot } from './components/Medical/AddDoctorSlot'
import AmountPaid from "./components/Medical/AmountPaid"
import { UpdatePatient } from './components/Appointments/UpdatePatient'
import { UpdateAppointment } from "./components/Appointments/UpdateAppointment"
import {AllAppointmentslist} from './components/Appointments/AllAppointmentslist'
import {SelectedAppointments} from './components/Appointments/SelectedAppointments'
//CSS
import './css/appointment.css';
import "./css/pharmacy.css";
import "./css/bootstrap.css";
import '../node_modules/bootstrap/js/dist/dropdown';

function Navbar(props) {
  const APIDate = useContext(TodayDate)

  const [addoption, setaddoption] = useState("none");
  const toggleaddoption = () => {
    if (addoption === "none") {
      setaddoption("block");
    } else if (addoption === "block") {
      setaddoption("none");
    }
  };
  const [patientform, setpatientform] = useState("none");
  const togglepatientform = () => {
    if (patientform === "none") {
      setpatientform("block");
    } else if (patientform === "block") {
      setpatientform("none");
    }
  };
  const [appointmentform, setappointmentform] = useState("none");
  const toggleappointmentform = () => {
    if (appointmentform === "none") {
      setappointmentform("block");
    } else if (appointmentform === "block") {
      setappointmentform("none");
    }
  };
  const [doctorform, setdoctorform] = useState("none");
  const toggledoctorform = () => {
    if (doctorform === "none") {
      setDocval(1)
      setdoctorform("block");

    } else if (doctorform === "block") {
      setDocval(0)
      setdoctorform("none");
    }
  };

  function formshift() {
    setappointmentform("none");
    setpatientform("block");
  }
  function logout() {
    localStorage.removeItem('email');
    localStorage.removeItem('name');
    localStorage.removeItem('id');
    localStorage.removeItem('designation');
    window.location.reload(true);
  }
  const [logoutbtn, setlogoutbtn] = useState('none');
  const togglelogoutbtn = () => {
    if (logoutbtn === 'none') {
      setlogoutbtn('block');
    }
    if (logoutbtn === 'block') {
      setlogoutbtn('none');
    }
  }


  const [Docval, setDocval] = useState()
  return (
    <>
      <button className={`parentclose d-none position-absolute`}></button>
      <div className="navsection p-2">
        <div className="container-fluid ">
          <div className="row align-items-center justify-content-evenly">
            <div className="col-lg-2 col-xl-2 col-md-2 col-sm-2 col-6">
              <div className="row">
                <div className="col-md-auto col-auto user">
                  <p className="m-0 username text-decoration-none text-lg-start text-md-start text-center"> {props.username} </p>
                  <p className="m-0 userstatus text-decoration-none text-lg-start text-md-start text-center"><small className="text-muted">{props.designation}</small> </p>
                </div>
                <div className="col-1 p-0 m-0 position-relative">
                  <button className='btn  p-0 m-0' onClick={togglelogoutbtn}><img src={process.env.PUBLIC_URL + "/images/more.png"} style={{ width: '1rem' }} /></button>
                  <button className={`logout d-${logoutbtn} btn button-brandy end-0 position-absolute`} onClick={logout}>Logout</button>
                </div>
              </div>
            </div>
            <div className="col-lg-6 col-xl-5 col-sm-auto col-md-auto col-10 p-2 m-0 menu order-1 order-xl-0 order-sm-0 order-md-0 order-sm-0">
              <div className="row align-items-center justify-content-around">
                <div className={`col-auto bg-seashell`}>
                  <Link to="/" className="text-decoration-none "> <div className="text-center"> <img src={process.env.PUBLIC_URL + "/images/today.png"} alt="displaying_image" className="img-fluid" style={{ width: `1.5rem` }} />{" "} <p className="col-12 m-0">Today</p> </div> </Link>
                </div>
                <div className={`col-auto bg-seashell`}>
                  <Link to="/Appointments" className="text-decoration-none">
                    <div className="text-center">
                      <img
                        src={process.env.PUBLIC_URL + "/images/appointment.png"}
                        alt="displaying_image"
                        className="img-fluid"
                        style={{ width: `1.5rem` }}
                      />
                      <p className="col-12 m-0">Appointments</p>
                    </div>
                  </Link>
                </div>
                <div className="col-auto">
                  <Link href="/#" className="text-decoration-none"> <div className="text-center"> <img src={process.env.PUBLIC_URL + "/images/patient.png"} alt="displaying_image" className="img-fluid" style={{ width: "1.5rem" }} /> <p className="col-12 m-0">Patients</p> </div> </Link>
                </div>
                <div className="col-auto">
                  <Link href="/#" className="text-decoration-none"> <div className="text-center"> <img src={process.env.PUBLIC_URL + "/images/doctor.png"} alt="displaying_image" className="img-fluid" style={{ width: "1.5rem" }} /> <p className="col-12 m-0">Doctors</p> </div> </Link>
                </div>
                <div className="col-auto">
                  <Link href="/#" className="text-decoration-none"> <div className="text-center"> <img src={process.env.PUBLIC_URL + "/images/dsr.png"} alt="displaying_image" className="img-fluid" style={{ width: "1.5rem" }} /> <p className="col-12 m-0">DSR</p> </div> </Link>
                </div>
                <div className="col-auto">
                  <Link to="/Pharmacy" className="text-decoration-none"> <div className="text-center"> <img src={process.env.PUBLIC_URL + "/images/pharmacy.png"} alt="displaying_image" className="img-fluid" style={{ width: "1.5rem" }} /> <p className="col-12 m-0">Pharmacy</p> </div> </Link>
                </div>
              </div>
            </div>
            <div className="col-lg-2 col-xl-1 col-md-1 col-6 col-sm-2 text-center position-relative">
              <button className="btn col-12 addbtn" onClick={toggleaddoption}> {" "} +Add{" "} </button>
              <div className={`text-center addoptions d-${addoption} position-absolute`} >
                <input className="col-12 p-lg-2 border-1 border-bottom text-start patient" type="button" defaultValue="Patient" onClick={togglepatientform} />
                <input className="col-12 p-lg-2 text-start border-1 border-bottom appointment" type="button" defaultValue="Appointment" onClick={toggleappointmentform} />
                <input className="col-12 p-lg-2 doctorslot shadow-sm text-start" type="button" defaultValue="Doctor Slot" onClick={toggledoctorform} />
              </div>
            </div>
            <div className="col-10 col-lg-2 col-xl-2 col-md-3 col-sm-2 py-3  order-sm-2 order-2 Search position-relative">
              <article className="Search-box">
                <input
                  type="text"
                  className="Search-Text"
                  placeholder="search"
                />
                <img
                  src={process.env.PUBLIC_URL + "/images/search.png"}
                  alt="displaying_image"
                  className="p-0 m-0 rounded-5"
                  style={{ width: "2rem" }}
                />
              </article>
            </div>
          </div>
        </div>
      </div>

      <div className={`col-lg-5 col-md-6 col-sm-12 col-12 patientinfosection d-${patientform} border-start border-top border-2 position-absolute`} >
        <AddPatient togglepatientform={togglepatientform} />
      </div>


      <div className={`col-lg-5 col-md-6 col-sm-12 col-12 appointmentinfosection d-${appointmentform} border-start border-top border-2 position-absolute`} >
        <AddAppointment toggleappointmentform={toggleappointmentform} formshift={formshift} />
      </div>
      {
        Docval == 1 ? (
          <div className={`col-lg-5 col-md-6 col-sm-12 col-12 doctorinfosection d-${doctorform}  border-start border-top border-2 position-absolute`} >
            <AddDoctorSlot toggledoctorform={toggledoctorform} staticBackdrop4={'staticBackdrop3'} />

          </div>
        ) : (
          <div></div>
        )
      }

    </>
  );
}


function Doctorsection() {
  const url = useContext(URL);
  const APIDate = useContext(TodayDate)

  const month = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December",];
  var weekday = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday",];
  const d = new Date();
  const date = d.getDate() < 10 ? '0' + d.getDate() : d.getDate();
  const monthcount = d.getMonth() + 1;
  const yearcount = d.getFullYear();
  let monthname = month[d.getMonth()];
  var weekname = weekday[d.getDay()];
  var fullDate = new Date();
  var currentDate = "Today" + " " + monthname + " " + fullDate.getDate() + "," + fullDate.getFullYear() + " ";

  let Doctorarray = [];
  let TodayDoctors = [];
  const [isLoading, setIsLoading] = useState(false);
  const [docapi, setdocapi] = useState([]);
  const [todayDoc, settodaydoc] = useState([]);
  async function fetchapi() {
    setIsLoading(true);
    await axios.get("/doctor/list").then(function (response) {
      let tempArray = response.data.data;
      for (let i = 0; i < tempArray.length; i++) {
        Doctorarray.push([tempArray[i].id, tempArray[i].doctor_name, []]);
        for (var j = 0; j < tempArray[i].month_timeslots.length; j++) {
          if (tempArray[i].month_timeslots[j].date === APIDate) {
            Doctorarray[i][2].push([[tempArray[i].month_timeslots[j].time_from], [tempArray[i].month_timeslots[j].booking_status]])
          }
        }
      }
      for (var q = 0; q < Doctorarray.length; q++) {
        if (Doctorarray[q][2].length != 0) {
          TodayDoctors.push(Doctorarray[q])
          settodaydoc(TodayDoctors)
        }
      }
      if (TodayDoctors.length == 0) {
        TodayDoctors.push(['0', 'No Doctors Found', ['No Time Slots Found']]);
        settodaydoc(TodayDoctors);
      }
      setdocapi(Doctorarray);
    });
    setIsLoading(false);
  }

  useEffect(() => {
    fetchapi()
  }, [])

  const [Doctor, setDoctor] = useState(0);
  function _selectedDoctor(_selected) {
    for (var s = 0; s < docapi.length; s++) {
      if (_selected === s && isLoading == false) {
        return (

          <>
            <div className="p-0 m-0">
              <Timecard docid={todayDoc[_selected][0]} _selected={_selected} />
            </div>
            <div className="p-0 m-0">
              <DoctorSchedule todayDoc={todayDoc} _selected={_selected} />
            </div>
            <hr />
          </>
        );

      }
    }
  }

  function Time() {
    const [time, setTime] = useState(new Date());
    useEffect(() => {
      setInterval(() => {
        setTime(new Date());
      }, 1000);
    }, []);

    return (
      <span>
        {time.toLocaleString("en-US", {
          timeStyle: "medium",
          hour12: false,
        })}{" "}
      </span>
    );
  }

  const [doctorform, setdoctorform] = useState("none");
  const toggledoctorform = () => {
    if (doctorform === "none") {
      setDocval(1)
      setdoctorform("block");
    }
    if (doctorform === "block") {
      setDocval(0)
      setdoctorform("none");
    }
  };


  const [Docval, setDocval] = useState(0)
  return (
    <>
      <div className="container-fluid doctorcontainer">
        <section className="doctorsection ">
          <div className="row shadow-sm">
            <div className="d-flex scroll doctortab">
              {isLoading ? (
                <> <div className="spinner-border my-auto" style={{ width: "2rem", height: "2rem" }} role="status" >
                  <span className="sr-only"></span> </div>
                  <div className="spinner-grow my-auto" style={{ width: "1.5rem", height: "1.5rem" }} role="status" >
                    <span className="sr-only"></span> </div> </>
              ) : (
                todayDoc.map((data, i) => (
                  <>
                    <button className={`btn m-1 doctorbutton text-${i === Doctor ? "light" : "dark"} bg-${i === Doctor ? "charcoal" : "seashell"}`} onClick={(a) => { setDoctor(i) }}>{`${data[0]}.${data[1]}`}</button>
                  </>
                ))
              )}
              <button className="btn bg-transparent border-0 d-inline-flex" id="adddoctorbtn" onClick={toggledoctorform} >
                <img src={process.env.PUBLIC_URL + "/images/addicon.png"} alt="displaying_image" style={{ width: "2.5rem" }} />
              </button>
            </div>
          </div>
        </section>
      </div>
      <section className="patientsection border-start border-5 border-dark border-opacity-50 position-relative">
        <div className="container-fluid">
          <div className="col-12">
            <div className="row">
              <div className="col-9">
                <div className="p-1" id="calender">
                  {currentDate}
                </div>
              </div>
              <div className="col-3">
                <Draggable className="p-1 align-items-center">
                  <div className="text-black align-items-center" id="livetime">
                    {Time()}
                  </div>
                </Draggable>
              </div>
            </div>
          </div>
        </div>
        {todayDoc.length == 0 ? (
          <div className="container-fliud pt-3">
            <div className="d-flex fs-2 align-items-center justify-content-around">
              <strong className="text-burntumber">Please Wait...</strong>
              <div
                className="text-burntumber spinner-border ml-auto"
                role="status"
                aria-hidden="true"
              ></div>
            </div>
          </div>
        ) : (
          _selectedDoctor(Doctor)
        )}
      </section>
      {
        Docval == 1 ? (
          <div className={`col-lg-5 col-md-6 col-sm-12 col-12 doctorinfosection d-${doctorform}  border-start border-top border-2 position-absolute`} >
            <AddDoctorSlot toggledoctorform={toggledoctorform} staticBackdrop4={'staticBackdrop4'} fetchapi={fetchapi} />
          </div>
        ) : (
          <div></div>
        )
      }

    </>
  );

}




function AllAppointments() {

  const docnames = useContext(DoctorsList)

  const [doctorid, setdoctorid] = useState()
  const [fromdate, setfromdate] = useState()
  const [todate, settodate] = useState()


  function tConvert(time) {

    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) {
      time = time.slice(1);
      time[3] = +time[0] < 12 ? ' AM ' : ' PM ';
      time[0] = +time[0] % 12 || 12;
    }
    return time.join('');
  }


  let array = [[1, 'Pending', 'lightred'], [2, 'Booked', 'lightblue'], [3, 'Cancelled', 'lightred'], [4, 'QR Generated', 'light'], [5, 'Checked_in', 'brandy'], [6, 'Vitals Done', 'lightred'], [7, 'In_apppointment', 'lightyellow'], [8, 'Payment done', 'success'], [9, 'Unattained', 'lightyellow'], [10, 'Completed', 'lightgreen']]

  function status(number) {
    let status
    for (let i = 0; i < array.length; i++) {
      if (number == array[i][0]) {
        status = array[i][1]
        break;
      }
    }
    return status
  }

  function status_color(number) {
    let status_color;
    for (let j = 0; j < array.length; j++) {
      if (number == array[j][0]) {
        status_color = array[j][2]
        break;
      }
    }
    return status_color
  }

  const [options,setoptions]  = useState([['All',''],['Cancelled',3],['Completed',10],['Unattended',9]])
  const [optionsindex,setoptionsindex] = useState(0)
  const [type,settype] = useState()
  return (
    <>
      <section className="page2appointment ">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-8">
              <div className="col-12">
                <h3 className="p-2">All Appointments</h3>
              </div>
              <div className="col-12">
                <div className="row g-3">
                  {
                    options.map((data,index)=>(
                      <div className="col-auto"><button className={`button px-4 rounded-5 button-${optionsindex == index ? 'seashell':'charcoal'} shadow-${optionsindex == index?'lg':'sm'}`} key={index} onClick={(e)=>{setoptionsindex(index);settype(data[1])}}>{data[0]}</button></div>
                    ))
                  }
                </div>
              </div>
            </div>
            <div className="col-md-4 daterange">
              <div className="col-12 mt-3 mb-2">
                <img src={process.env.PUBLIC_URL + "/images/today.png"} alt="displaying_image" style={{ width: "2rem" }} />
                <span className="daterangetitle">Select Date Range</span>
                <button className="float-end button-lightbrown">Clear</button>
              </div>
              <div className="d-flex g-md-3">
                <input placeholder="Start Date" className="form-control" type="date" onFocus={(e) => { setfromdate(e.target.value) }} />
                <div className="text-center">_</div>
                <input placeholder="End Date" className="form-control" type="date" onFocus={(e) => { settodate(e.target.value) }} />
              </div>
              <div className="col-12 mt-2">
                <h6 className="text-burntumber bold fw-bolder">Select Doctor to see their appointments</h6>
                <select className="form-control" onChange={(e) => { setdoctorid(e.target.value) }}>
                  <option disabled selected value="Select Doctor">Select Doctor</option>
                  {
                    docnames.map((response) => (
                      <option className="form-control" value={response[0]} >{response[0]}.{response[1]}</option>
                    ))
                  }
                </select>
              </div>
            </div>
          </div>
        </div>
        <div className="container-fluid pg_2 mt-5">
          <section className="page2allappointment scroll scroll-y">
              <h4>Appointments</h4>
                <table className="table datatable text-center position-relative">
                  <thead>
                    <tr>
                      <th>Update Details</th>
                      <th>Status</th> 
                      <th>Patient Name</th>
                      <th>Doctor Name</th>
                      <th>Phone Number</th>
                      <th>Date</th>
                      <th>Time</th>
                      <th>Total Amount</th>
                      <th>Amount Status</th>
                      <th>Rx</th>
                      <th>F/U Date</th>
                      <th>Actions</th>
                      <th>More</th>
                    </tr>
                  </thead>
                  <tbody>
                  {
                    doctorid ? (
                      <SelectedAppointments type={type} doctorid={doctorid} fromdate={fromdate} todate={todate} status={status} status_color={status_color} tConvert={tConvert}/>
                    ):(
                      <AllAppointmentslist type={type} doctorid={doctorid}  status={status} status_color={status_color} tConvert={tConvert}/>
                    )
                  }
                 </tbody>
                </table>
          </section>
        </div>
      </section>


    </>
  );
}

function Pharmacy() {
  let menu = ["Sale Orders", "Stock Info", "Purchase"];
  const [menuindex, setmenuindex] = useState(1);
  const [npef, setnpef] = useState("none");
  const [peidw, setpeidw] = useState("none");
  const [pharmacy, setpharmacy] = useState("block");

  const toggle_npef = () => {
    if (npef === "none") {
      setnpef("block");
      setpharmacy("none");
    }
    if (npef === "block") {
      setnpef("none");
      setpharmacy("block");
    }
  };
  const toggle_peidw = () => {
    if (peidw === "none") {
      setpeidw("block");
      setpharmacy("none");
    }
    if (peidw === "block") {
      setpeidw("none");
      setpharmacy("block");
    }
  };
  const _selectedmenu = (_menu) => {
    if (_menu === 0) {
      return <div className="">{_menu}</div>;
    }
    if (_menu === 1) {
      return (
        <div className="">
          <Stocksection />
        </div>
      );
    }
    if (_menu === 2) {
      return <Purchasesection func={toggle_npef} function={toggle_peidw} />;
    }
    return <div className="fs-2">Nothing Selected</div>;
  };
  return (
    <>
      <section className={`PEdetailssection position-absolute mt-1 d-${peidw} bg-seashell`} >
        {<PEitemdetailssection func={toggle_peidw} />}
      </section>

      <section className={`pharmacy position-relative d-${pharmacy}`}>
        <div className="pharmacysection">
          <div className="container-fluid pharmacytabsection">
            <div className="row py-2">
              {menu.map((e, i) => {
                return (
                  <div className="col-auto">
                    <button
                      className={`btn text-${i === menuindex ? "light" : "dark"
                        } bg-${i === menuindex ? "charcoal" : "seashell"}`}
                      onClick={(a) => setmenuindex(i)}
                    >
                      {e}
                    </button>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
        <div className="ps-md-3 pt-4 " id="calender">
          {<Livetime />}
        </div>
        <div className="p-0 m-0">{_selectedmenu(menuindex)}</div>
      </section>
      <section
        className={`newpurchaseentrysection position-absolute d-${npef}`}
      >
        {<Newpurchaseentryform func={toggle_npef} />}
      </section>
    </>
  );
}

