import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useContext } from 'react'
import { DoctorsList, URL, Doctorapi } from '../../index'
import Notiflix from 'notiflix';
import '../../css/bootstrap.css';
import '../../css/appointment.css';


const UpdateAppointment = (props) => {

    const url = useContext(URL);
    const DocApi = useContext(Doctorapi)
    const Doclist = useContext(DoctorsList)
    const [cliniclist, setcliniclist] = useState([])
    const [searchinput, setsearchinput] = useState()
    const [searchlist, setsearchlist] = useState([])
    const [displaysearchlist, setdisplaysearchlist] = useState('none')
    const [patientid, setpatientid] = useState()
    const [doctorid, setdoctorid] = useState()
    const [clinicid, setclinicid] = useState()
    const [time, settime] = useState()
    const[ischecked,setischecked] = useState()
    let adminid = localStorage.getItem('id')

    function ClinicList() {
        axios.get(`${url}/clinic/list`).then((response) => {
            setcliniclist(response.data.data)
        })
    }
    useEffect(() => {
        ClinicList()
    }, [])

    function tConvert(time) {

        time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

        if (time.length > 1) {
            time = time.slice(1);
            time[3] = +time[0] < 12 ? ' AM ' : ' PM ';
            time[0] = +time[0] % 12 || 12;
        }
        return time.join('');
    }


    
    const get_value = (e) => {
        e.preventDefault()
        setsearchinput(e.target.value)
        setpatientid(e.target.name)
        setdisplaysearchlist('none');
    }
    const gettime_value = (e) => {
        e.preventDefault()
        settime(e.target.value)
    }

    const [ApikeyDocTimeslots, setApikeyDocTimeslots] = useState()
    const [ApiDocTimefrom, setApiDocTimefrom] = useState();

    function getTimeslots(e) {
        e.preventDefault()
        setdoctorid(e.target.value)
        let Timeslots = [];
        setApikeyDocTimeslots([])
        for (let i = 0; i < DocApi.length; i++) {
            if (DocApi[i].id === Number(e.target.value)) {
                Timeslots.push(DocApi[i].month_timeslots)
            }
        }
        setApikeyDocTimeslots(Timeslots)
    }

    function getTimefrom(e) {
        e.preventDefault()
        let timefrom = []
        for (let j = 0; j < ApikeyDocTimeslots[0].length; j++) {
            if (ApikeyDocTimeslots[0][j].date === e.target.value) {
                timefrom.push([ApikeyDocTimeslots[0][j].id, ApikeyDocTimeslots[0][j].time_from, ApikeyDocTimeslots[0][j].booking_status])
            }
        }
        setApiDocTimefrom(timefrom)
    }

    function resetform(e) {
        e.preventDefault()
        setsearchinput()
        setpatientid()
        setdoctorid()
        setclinicid()
        setischecked()
    }

    function UpdateAppointment(e) {

            if (props.appointmentid && doctorid && clinicid && time && adminid) {
                   console.log(props.appointmentid,Number(doctorid),Number(clinicid),Number(time),Number(adminid),props.patientid)
                axios.post(`${url}/update/appointment`, {
                    appointment_id: props.appointmentid,
                    doctor_id: Number(doctorid),
                    clinic_id: Number(clinicid),
                    timeslot_id: Number(time),
                    patientid :props.patientid,
                    admin_id: Number(adminid)
                }).then((response) => {
                    if(response){
                        Notiflix.Notify.success(response.data.message);
                        console.log(response)
                        resetform() 
                    }else{
                        Notiflix.Notify.alert('Failed to Update')
                    }
               
                })
            } else {
                Notiflix.Notify.warning('Please Fill all Detais');
            }
           
    }
    const confirmmessage=(e)=>{
        e.preventDefault()
        Notiflix.Confirm.show(
            `Update Appointment Details`,
            `Do you surely want yo update ${props.patientname} Appointment Details`,
            'Yes',
            'No',
            () => {
                UpdateAppointment()
            },
            () => {
                return 0
            },
            {
            },
            );
    }
    return (    
        <section className='bg-seashell col-10 m-auto position-relative rounded-5 p-2 border-1 shadow-lg '>
            <h5 className="text-center mt-2">Update {props.patientname} Appointment Details</h5>
            <button type="button" className="btn-close closebtn position-absolute" aria-label="Close" onClick={props.toggleappointmentform} ></button>
            <hr />
            <form className="col-12">
                <label className="m-0 mb-2">Name of the Patient </label>
                <input type="text" className="form-control selectpatient col-10 position-relative" value={props.patientname}   />
                <div className={`col-8 d-${displaysearchlist} searchinput`}>
            <button className='col-12 p-0 m-0 border-0 bg-pearl text-charcoal text-start border border-1 shadow' onClick={get_value}></button>
                </div>
                <hr />
                <label>Select Location</label>
                <div className="col-12 form-control location bg-seashell  border-0" >
                    {
                        cliniclist.map((data) => (
                            <>
                                <label><input type="checkbox" className="radio form me-1" checked={ischecked} name={data.id} onClick={(e) => { setclinicid(e.target.name); setischecked(true);}} /> {data.title} {data.address}</label>
                                <br /></>   
                        ))
                    }
                </div>
                <hr />
                <div className="row p-0 m-0">
                    <div className="col-md-6">
                        <label>Select Doctor</label>
                        <div className="col-12">
                            <select className="col-10 form-control selectdoctor" onChange={getTimeslots}>
                                <option defaultValue="Select Doctor" >Select Doctor</option>
                                {
                                    Doclist.map((data) => (
                                        <option className='text-charcoal' value={data[0]}>{data[0]}.{data[1]}</option>
                                    ))
                                }
                            </select>
                        </div>
                    </div>
                    <div className="col-md-6">
                        <label className="">Select Date</label>
                        <div className="col-12"><input type="date" className="form-control selectdate" onChange={getTimefrom} />
                        </div>
                    </div>  
                </div>

                <hr />

                <p className="m-0 mb-2">Select Time Slot</p>
                <div className="scroll align-items-center justify-content-around col-12">
                    {ApiDocTimefrom ? (
                        <>
                            {
                                ApiDocTimefrom.map((data) => (
                                    data[2] == 0 ? (
                                        <button className="btn button-lightgreen m-1" value={data[0]} onClick={gettime_value}>{tConvert(data[1])}</button>
                                    ) : (
                                        <button disabled className="btn button-burntumber m-1" value={data[0]}>{tConvert(data[1])}</button>
                                    )

                                ))
                            }
                            <button className="btn btn-sm done m-1">
                                <img src="/images/addicon.png" alt="displaying_image" className="mb-1 me-1" style={{ width: "1.2rem" }} /> Time Slot
                            </button>

                        </>
                    ) : (
                        <div className='p-2 rounded'>Choose Doctor and Date to get Time Slots</div>
                    )
                    }
                </div>
                <hr />
                <div className="col-6 py-2 pb-2 m-auto text-center">
                    {/* <button type="button"  data-bs-toggle="modal" data-bs-target={`#staticBackdrop${props.key}`} className="btn done px-5" onClick={BookAppointment}> Done </button> */}
                <button className='btn button-burntumber' onClick={confirmmessage} >Done</button>
                </div>
                <div className="col-6 pb-2 m-auto text-center">
                    <button className="btn btn-light px-5" onClick={resetform}>Reset</button>
                </div>
            </form>

            {/* <div className="modal fade" id={`staticBackdrop${props.key}`} data-bs-backdrop="static" data-bs-keyboard="false" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="staticBackdropLabel">Modal title</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body"> Do you Want to update Appointment details {props.patientname} </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" value='false' data-bs-dismiss="modal" onClick={BookAppointment}>Close</button>
                            <button type="button" className="btn btn-primary" value='true' data-bs-dismiss="modal" onClick={BookAppointment}>Proceed</button>
                        </div>
                    </div>
                </div>
            </div> */}
        </section>

    )
}
export { UpdateAppointment }
