          <button className="btn btn-close" onClick={() => { openchat == 'none' ? setopenchat('block') : setopenchat('none') }}></button>
          <input className="bg-seashell rounded-2 border border-1" ref={chatinputref} onBlur={(e) => { setchat(e.target.value) }} />
