    <table className="table datatable text-center"><thead>
      <tr>
        <th>No.</th>
        <th>Item Name</th>
        <th>Total Qty</th>
        <th>Amount</th>
        <th>Last Vendor</th>
        <th>Add</th>
        <th>Delete</th>
      </tr>
    </thead>
      <tbody>
        {<POitemdetailsarray />}
      </tbody>
    </table>
  )
}
function PEitemdetailssection(props) {
  console.log(props.purchaseentryarr)
  return (<>
    <div className="container-fluid p-0 m-0 bg-seashell ">
      <div className="container-fluid bg-seashell border border-2 border-top-0 border-start-0 border-end-0 ">
        <div className="row p-2">
          <div className="col-1">
            <button type="button" className="btn-close closebtn m-auto" onClick={props.toggle_peidw} aria-label="Close"></button>
          </div>
          <div className="col-9">
            <h6 className='text-center' style={{ color: 'var(--charcoal)', fontWeight: '600' }}>{props.itembillid} Purchase Entry Item Details</h6>
          </div>
          <div className="col-2">
            <div className=' position-relative searchbutton' style={{ top: '0.25rem', right: '1rem' }}>
              <input type="text" className=" form-control d-inline PEsearch bg-seashell" placeholder="Search PE" />
              <button className="btn p-0 m-0 bg-transparent border-0 position-absolute" style={{ width: '2rem', right: '0', left: '0', top: '0.25rem' }}><img src={process.env.PUBLIC_URL + "/images/search.png"} alt="displaying_image" className="img-fluid p-0 m-0" /></button>
            </div>
          </div>
        </div>
      </div>
    </div>
    <div className='row'>
      <button className="col-3">Medicine</button>
      <button className="col-3"></button>
    </div>
    <div className='scroll bg-seashell scroll-y' style={{minHeight:'82vh',maxHeight:'82vh'}}>
    <table className="table datatable table-responsive text-center bg-seashell"><thead>
      <tr>
        <th className='border'>Item ID</th>
        <th className='border'>Item Name</th>
        <th className='border'>Batch No.</th>
        <th className='border'>Expiry Date</th>
        <th className='border'>MRP</th>
        <th className='border'>Rate</th>
        <th className='border'>Disc</th>
        <th className='border'>Trade Disc</th>
        <th className='border'>SGST</th>
        <th className='border'>CGST</th>
        <th className='border'>IGST</th>
        <th className='border'>Cost</th>
        <th className='border'>Qty.</th>
        <th className='border'>Total</th>
        <th className='border'>Print QR</th>
      </tr>
    </thead>
      {
      props.purchaseentryarr.medicines && props.purchaseentryarr.medicines.length!==0?(
        <tbody className='border'>
          {
          props.purchaseentryarr.medicines.map((item, _key) => (
         <tr className='border' key={_key}>
         <td className='border'>{item && item.id!==null ?item.id:'N/A'}</td>
         <td className='border'>{item.medicine && item.medicine.name !==null ?item.medicine.name:'N/A' }</td>
         <td className='border'>{item.batch_no && item.batch_no !=null ? item.batch_no :'N/A'}</td>
         <td className='border'>{item.expiry_date &&item.expiry_date !=null ?item.expiry_date:'N/A' }</td>
         <td className='border'>{item.mrp? "Rs. " +item.mrp:'N/A'}</td>
         <td className='border'>{item.rate ?"Rs. " + item.rate :'N/A'}</td>
         <td className='border'>{item.discount?item.discount + "%":'N/A'}</td>
         <td className='border'>{item.trade_discount ?item.trade_discount+ "%":'N/A' }</td>
         <td className='border'>{item.SGST_rate? item.SGST_rate +"%":'N/A'} | {item.SGST?"Rs. "+ item.SGST:'N/A'}</td>
         <td className='border'>{item.CGST_rate?item.CGST_rate+"%":'N/A'} | {item.CGST?"Rs. "+ item.CGST:'N/A'}</td>
         <td className='border'>{item.IGST?"Rs. "+ item.IGST:'N/A'}</td>
         <td className='border'>{item.cost?"Rs. "+ item.cost:'N/A'}</td>
         <td className='border'>{item.qty?item.qty:'N/A'}</td>
         <td className='border'>{item.total_amount ?item.total_amount :'N/A'}</td>
         <td className='border'><button className='btn'><img src={process.env.PUBLIC_URL + "/images/qrcode.png"} alt="displaying_image" style={{ width: "1.5rem" }} className="me-1" /></button></td>
     </tr>
      ))
          }

     </tbody>

      ):(
        <body className='text-center p-0 m-0 border border-1 '>
        <div className='position-absolute border-0 start-0 end-0 mx-3 p-2 bg-lightred'>
            <strong className='fs-5 text-center bg-lightred'>No Medicines Found</strong>
        </div>

      </body>
      )


    }
    </table>
    </div>
  </>
  )
}

