import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useContext } from 'react'
import { DoctorsList, URL, Doctorapi } from '../../index'
const AddAppointment = (props) => {


    const url = useContext(URL);
    const DocApi = useContext(Doctorapi)
    const Doclist = useContext(DoctorsList)
    const [doctors, setdoctors] = useState(0)
    const [cliniclist, setcliniclist] = useState([])
    const [searchinput, setsearchinput] = useState()
    const [searchlist, setsearchlist] = useState([])
    const [displaysearchlist, setdisplaysearchlist] = useState('none')
    const [patientid, setpatientid] = useState()
    const [doctorid, setdoctorid] = useState()
    const [clinicid, setclinicid] = useState()
    const [date, setdate] = useState()
    let adminid = localStorage.getItem('id')
    function ClinicList() {
        axios.get(`${url}/clinic/list`).then((response) => {
            setcliniclist(response.data.data)
        })
    }
    useEffect(() => {
        ClinicList()
    }, [])

    const searchpatient = (e) => {
        setsearchinput(e.target.value)
        axios.get(`${url}/patient/list?search=${searchinput}&limit=5&offset=0`).then((response) => {
            setsearchlist(response.data.data)
        })
        if (searchinput && searchinput.length > 1) {
            setdisplaysearchlist('block');
        } else {
            setdisplaysearchlist('none');
        }
    }
    const get_value = (e) => {
        e.preventDefault()
        setsearchinput(e.target.value)
        setpatientid(e.target.name)
        setdisplaysearchlist('none');
    }

    const [ApikeyDocTimeslots, setApikeyDocTimeslots] = useState([])
    const [ApiDocTimefrom, setApiDocTimefrom] = useState([]);

     function getTimeslots() {
            Number(doctors);
        for (let i = 0; i < DocApi.length; i++) {
            if (DocApi[i].id === doctors) {
                setApikeyDocTimeslots(DocApi[i].month_timeslots)
            }
        }
    }

     function getTimefrom(e) {
        setdoctors(e.target.value)
        for (let j = 0; j < ApikeyDocTimeslots; j++) {
            if (date && ApikeyDocTimeslots[j].date == date) {
                setApiDocTimefrom(ApikeyDocTimeslots[j].date, ApikeyDocTimeslots[j].time_from)
       
            }
        }
    }

    function BookAppointment(e) {
        e.preventDefault();
        axios.post(`${url}/add/appointment`, {
            patient_id: patientid,
            doctor_id: doctorid,
            clinic_id: clinicid,
            admin_id: adminid
        })
    }

    return (
        <>
            <h5 className="text-center mt-2">New Appointment</h5>
            <button type="button" className="btn-close closebtn position-absolute" aria-label="Close" onClick={props.toggleappointmentform} ></button>
            <hr />
            <form className="col-12">
                <label className="m-0 mb-2">Search Using Phone or Name</label>
                <input type="text" className="form-control selectpatient col-10 position-relative" value={searchinput ? searchinput : ''} onChange={searchpatient} />
                <div className={`col-8 d-${displaysearchlist} searchinput`}>
                    {
                        searchlist.map((data) => (
                            <button className='col-12 p-0 m-0 border-0 bg-pearl text-charcoal text-start border border-1 shadow' name={data.id} value={data.full_name} onClick={get_value}>{data.full_name}  {data.phone_number}</button>
                        ))
                    }
                </div>
                {
                    searchlist && searchlist.length == 0 ? (
                        <p className="text-danger btn fs-6 p-0 m-0">Patient not found. Add as new to book an Appointment</p>
                    ) : (
                        <p className="text-danger btn fs-6 p-0 m-0"></p>
                    )
                }

                <div className="col-12 p-0">
                    <a href="/#" className="btn text-decoration-none btn-sm done" onClick={props.formshift}> Add User </a>
                </div>
                <hr />
                <label>Select Location</label>
                <div className="col-12 form-control location scroll border-0" style={{ height: "7rem" }} >
                    {
                        cliniclist.map((data) => (
                            <>
                                <label><input type="checkbox" className="radio form me-1" name="check" value={data.id} onClick={(e) => { setclinicid(e.target.value) }} /> {data.title} {data.address}</label>
                                <br /></>
                        ))
                    }
                </div>
                <hr />
                <label>Select Doctor</label>
                <div className="col-12">
                    <select className="col-10 form-control selectdoctor" onChange={getTimeslots}>
                        <option defaultValue="Select Doctor">Select Doctor</option>
                        {
                            Doclist.map((data) => (
                                <option className='text-charcoal' value={data[0]}>{data[0]}.{data[1]}</option>
                            ))
                        }
                    </select>
                </div>
                <label className="mt-2">Select Date</label>
                <div className="col-12"><input type="date" className="form-control selectdate" onChange={(e) => { setdate(e.target.value); console.log(date); getTimefrom() }} /> </div>
                <hr />

                <p className="m-0 mb-2">Select Time Slot</p>
                <div className="scroll timetab col-12"></div>
                <button className="btn btn-sm done mt-3 ms-2" onClick={(e) => { e.preventDefault(); getTimefrom() }}> <img src="/images/addicon.png" alt="displaying_image" className="mb-1 me-1" style={{ width: "1.2rem" }} /> Time Slot </button>
                <hr />
                <div className="col-10 patientsearch m-auto">
                    <label htmlFor="patientsearch">Select Patient</label>
                    <input className="form-control position-relative " type="search" placeholder="Search Patient" aria-label="Search" /> <button className="btn btn-sm patientsearchbtn rounded m-0 position-absolute" type="submit" > <i className="fa fa-search" aria-hidden="true"></i> </button>
                </div>
                <hr />

                <div className="col-10 m-auto py-2 text-center">
                    <button type="submit" className="btn done px-5"> Done </button>
                </div>
                <div className="col-10 py-2 m-auto text-center">
                    <button className="btn btn-light px-5">Reset</button>
                </div>
            </form>
        </>

    )
}
export { AddAppointment }
