                    <h6 className="p-0 m-0 ms-3 fw-bold">Invoice Date</h6>
                    <input type="date" className="form-control ms-2 rounded-1" value={invoicedate ? invoicedate : ''} onChange={(e) => { setinvoicedate(e.target.value) }} style={{ color: "gray" }} />
